<template>  <div v-show="show" class="back-top" @click="toTop">    <MyIcon name="fanhuidingbu1" size="0.45rem" color="#fff" />  </div></template><script>export default {  name: 'BackTop',  data() {    return {      show: false    }  },  mounted() {    window.addEventListener('scroll', this.handleScroll, true)  },  methods: {    handleScroll() {      let scrolltop = document.documentElement.scrollTop || document.body.scrollTop      this.show = scrolltop > 400    },    toTop() {      let top = document.documentElement.scrollTop || document.body.scrollTop      //实现滚动效果      const timeTop = setInterval(() => {        document.body.scrollTop = document.documentElement.scrollTop = top -= 50        if (top <= 0) {          clearInterval(timeTop)        }      }, 10)    },  },}</script><style lang="scss" scoped>.back-top {  width: 0.8rem;  height: 0.8rem;  border-radius: 50%;  text-align: center;  line-height: 0.8rem;  background: #15a98d;  z-index: 100;  position: fixed;  bottom: 3rem;  right: 0.5rem;}</style>