<template>  <div class="notice-picture" v-if="value">    <swiper class="my-swiper" :options="swiperOption" ref="mySwiper">      <swiper-slide v-for="item in list" :key="item.id">        <div class="box">          <img :src="item.url" alt="" />        </div>      </swiper-slide>      <template v-slot:button-prev>        <div class="swiper-button-prev">          <MyIcon name="arrowLeft" color="#fff" size="0.28rem" />        </div>      </template>      <template v-slot:button-next>        <div class="swiper-button-next">          <MyIcon name="arrowLeft" color="#fff" size="0.28rem" />        </div>      </template>    </swiper>    <div class="close" @click="hide">      <MyIcon name="guanbi-" size="0.28rem" color="#fff" />    </div>    <div class="bg" @click="hide"></div>  </div></template><script>export default {  name: 'NoticePicture',  props: {    list: Array,    value: Boolean,  },  data() {    return {      swiperOption: {        slidesPerView: 'auto',        navigation: {          nextEl: '.swiper-button-next',          prevEl: '.swiper-button-prev',        },      },    }  },  methods: {    hide() {      this.$emit('input', false)    },  },}</script><style lang="scss" scoped>.notice-picture {  position: fixed;  top: 0;  left: 0;  height: 100vh;  width: 100%;  z-index: 999;  .bg {    height: 100%;    width: 100%;    background: #000;  }  .close {    position: absolute;    right: 0.2rem;    top: 0.2rem;  }  .my-swiper {    width: 100%;    height: 8rem;    position: absolute;    top: 50%;    margin-top: -4rem;    .box {      width: 100%;      height: 100%;      background: rgba(255, 255, 255, 0.4);      position: relative;      img {        width: 100%;        height: 100%;      }    }  }  .swiper-button-prev,  .swiper-button-next {    width: 0.5rem;    height: 0.8rem;    background: rgba(206, 206, 206, 0.6);    display: flex;    align-items: center;    justify-content: center;  }  .swiper-button-next {    i {      transform: rotate(180deg);    }  }}</style>