<template>  <div class="ui5-pager-wrap invite-page">    <!--分页功能-->    <el-pagination      @size-change="sizeChange"      @current-change="currentChange"      :current-page="pagination.page"      :page-size="pagination.limit"      :page-sizes="pagination.sizes"      :total="pagination.total"      layout="total, sizes, prev, pager, next, jumper" />  </div></template><script>export default {  name: 'PageCom',  props: {    pagination: {      type: Object,      default() {        return {          page: 1,          limit: 10,          total: 0,          sizes: [5, 10 ,15, 20]        }      }    }  },  data () {    return {      newPagination: {}    }  },  mounted () {    this.newPagination = JSON.parse(JSON.stringify(this.pagination))  },  methods: {    /*     * 分页功能的操作     * */    sizeChange (val) {      this.newPagination.limit = val      this.newPagination.page = 1      this.$emit('pageChange', this.newPagination)    },    currentChange (val) {      this.newPagination.page = val      this.$emit('pageChange', this.newPagination)    }  }}</script><style scoped>.ui5-pager-wrap {  text-align: center;  padding: 10px 0 60px;}</style>