<template>  <div class="footer-index">    <!-- <div class="third-party">      <div class="icon" v-for="item in thirdList" :key="item.id">        <MyIcon :name="item.icon" size="0.48rem" color="#FFFFFF" />      </div>    </div> -->    <div class="info">      <span @click="jump(item)" v-for="item in tab" :key="item.id">        {{ item.name }}      </span>    </div>    <div class="phone">      <p>旅游热线: 86-28-83510033 爱心捐赠: 86-28-83505513</p>      <p>失物招领、投诉电话: 86-28-83507814 导游预约电话: 86-28-83516748</p>      <p>救援电话: 86-28-83516808 传真: 86-28-83516911</p>    </div>    <div class="email">邮箱: pandabase@panda.org.cn 地址: 中国四川省成都市成华区熊猫大道1375号</div>    <div class="copyright">Copyright ? 2001-2023 panda.org.cn 成都大熊猫繁育研究基地版权所有</div>    <a class="record" href="http://www.beian.gov.cn/portal/registerSystemInfo?recordcode=51010802001155"    ><img src="https://file.daqsoft.com/uploadfile/server/geekui/panda/H5/wanbei.png" alt="" /> 川公网安备 51140202000125号</a    >    <a href="https://www.daqsoft.com/" class="support">技术支持：成都中科大旗软件股份有限公司</a>    <a href="https://bszs.conac.cn/sitename?method=show&id=97C3D4D49BF047DEE05310291AAC2F69"><img class="logo" src="https://file.daqsoft.com/uploadfile/server/geekui/panda/H5/blue.png" alt="" /></a>  </div></template><script>import { getNewsList } from '@/service/apiList/common'export default {  name: 'FooterIndex',  inject: ['jumpDetails'],  data() {    return {      thirdList: [{ icon: 'weixin' }, { icon: 'weibo' }, { icon: 'douyin' }, { icon: 'guanfangAPP' }],      tab: [        {          name: '关于我们',          path: '/h5/about',        },        {          name: '联系我们',          path: '/details?details=lxwm&channelCode=ykfw',        },        {          name: '爱心支持',          path: '/h5/support',        },        {          name: '企业邮箱登陆',          jumpType: 1,          path: 'http://mail.panda.org.cn/',        },      ],    }  },  methods: {    jump(item) {      if (item.jumpType === 1) {        location.href = item.path      } else if (item.path && item.path.includes('?details')) {        const params = this.getQueryString(item.path)        getNewsList({ channelCode: params.details }).then((res) => {          try {            this.jumpDetails(res.datas[0], params.channelCode)          } catch (error) {            return ''          }        })      } else if (item.path) {        this.$router.push(item.path)      }    },    getQueryString(url) {      let ItemArr = []      let ItemObj = {}      url        .substring(url.lastIndexOf('?') + 1, url.length)        .split('&')        .map((item) => {          ItemArr.push(item.split('='))        })      ItemArr.map((item) => {        ItemObj[item[0]] = item[1]      })      return ItemObj    },  },}</script><style lang="scss" scoped>.footer-index {  width: 7.5rem;  height: 5.8rem;  background: #000;  text-align: center;  padding-top: 0.79rem;  .third-party {    display: flex;    justify-content: space-between;    padding: 0.7rem 1.35rem 0.68rem;    .icon {      width: 0.9rem;      height: 0.9rem;      background: rgba(55, 55, 55, 1);      border-radius: 50%;      line-height: 0.9rem;    }  }  .info {    span {      color: #ffffff;      font-size: 0.2rem;      position: relative;      padding: 0 0.2rem;      line-height: 1;      &:not(&:last-child) {        &::after {          content: '';          position: absolute;          top: 0.02rem;          right: 0;          width: 0.01rem;          height: 0.2rem;          background: #ffffff;        }      }    }  }  .phone {    margin-top: 0.4rem;    p {      font-weight: 500;      line-height: 0.34rem;      font-size: 0.2rem;      color: #999999;    }  }  .email {    margin-top: 0.17rem;    font-size: 0.2rem;    color: #999999;    line-height: 0.32rem;  }  .copyright {    margin-top: 0.38rem;    font-weight: 500;    font-size: 0.2rem;    color: #999;    line-height: 1;  }  .record {    margin-top: 0.25rem;    font-weight: 500;    font-size: 0.2rem;    color: #999999;    line-height: 1;    display: flex;    align-items: center;    justify-content: center;    img {      width: 0.2rem;      margin-right: 0.09rem;    }  }  .support {    line-height: 1;    margin-top: 0.24rem;    font-weight: 500;    font-size: 0.2rem;    color: #999;    display: block;  }  .logo {    margin-top: 0.23rem;    width: 0.34rem;  }}</style>