<template>  <div class="footer-wrap">    <div class="top">      <div class="left">        <p class="title">住所</p>        <p class="address">中国四川省成都市成A区パンダ大通り1375号</p>        <p class="postal-code">]政番号:610081</p>        <p class="mail">メ`ルアドレス:pandabase@panda.org.cn</p>      </div>      <div class="right">        <p class="title">ホットライン</p>        <p class="tel-one">86-28-83510033</p>        <p class="tel-two">ファックス:86-28-83516911</p>      </div>      <!--<div class="to-top" @click="toTop">        <MyIcon name="top" color="#333" size="0.44rem" />      </div>-->      <el-backtop>        <MyIcon name="top" color="#333" size="0.44rem" />      </el-backtop>    </div>    <div class="bottom">      <div class="left" @click="jump">{{ contactUs }}</div>      <div class="right">?2001-2023 panda.org.cn 成都ジャイアントパンダ繁殖研究基地 All Rights Reserved.</div>    </div>  </div></template><script>import { channelcrumbs } from '@/service/apiList/common'export default {  name: 'FooterCom',  data() {    return {      contactUs: 'おい合わせ'    }  },  methods: {    toTop() {      let top = document.documentElement.scrollTop || document.body.scrollTop      //实现滚动效果      const timeTop = setInterval(() => {        document.body.scrollTop = document.documentElement.scrollTop = top -= 50        if (top <= 0) {          clearInterval(timeTop)        }      }, 5)    },    jump() {      channelcrumbs({        channelCode: 'about'      }).then(res => {        if(res.code === 0) {          res.data.subset.forEach(item => {            if(item.name.includes(this.contactUs)) {              this.$router.push(item.url1)            }          })        }      })    }  }}</script><style lang="scss" scoped>.footer-wrap {  width: 100%;  height: 5.4rem;  padding: 1rem 1.2rem 0 1.2rem;  background: #ffffff;  .top {    display: flex;    position: relative;    .left {      color: #333333;      font-size: .18rem;      .title {        font-size: 0.36rem;      }      .address {        margin: .35rem 0 .64rem;      }      .mail {        margin-top: .25rem;      }    }    .right {      color: #333333;      margin-left: 3.37rem;      .title {        font-size: .36rem;      }      .tel-one {        font-size: .5rem;        font-weight: bold;        margin: .38rem 0 .43rem;      }      .tel-two {        font-size: .18rem;      }    }    .to-top{      cursor: pointer;      width: 0.7rem;      height: 0.7rem;      border: 0.02rem solid #7E7E7E;      border-radius: 0.01rem;      text-align: center;      line-height: 0.7rem;      position: absolute;      right: 0;      top: 0;      transition: all .25s;      i {        transition: all .25s;      }      &:hover {        background: #15A98D;        border-color: #15A98D;        i {          color: #ffffff !important;        }      }    }    .el-backtop {      cursor: pointer;      width: 0.7rem;      height: 0.7rem;      background: #b2b2b2;      border-radius: 0.01rem;      transition: all .25s;      i {        color: #ffffff !important;      }      &:hover {        background: #15a98d;      }    }  }  .bottom {    display: flex;    justify-content: space-between;    align-items: center;    margin-top: .6rem;    .left {      color: #333333;      font-size: 0.24rem;      text-align: center;      width: 2.08rem;      height: 0.7rem;      line-height: 0.7rem;      border: 0.02rem solid #7E7E7E;      transition: all .25s;      cursor: pointer;      &:hover {        color: #ffffff;        background: #15A98D;        border-color: #15A98D;      }    }    .right {      font-size: .18rem;      color: #333333;    }  }}</style>