<template>  <div class="footer-wrap">    <div class="jump">      <div v-for="(item, index) in jumpList" :key="index" class="jump-list" v-show="item.mediaImage">        <el-popover          placement="top"          popper-class="prpper-padding"          width="180"          trigger="hover">          <div class="qr-code">            <img :src="item.mediaImage" alt="" class="img" />          </div>          <img slot="reference" :src="item.imgUrl" alt="" />        </el-popover>      </div>    </div>    <div class="relation">      <div v-for="(item, index) in list" :key="index" class="relation-list" @click="detail(item)">        <span class="name">{{item.name}}</span>        <span class="line" v-if="index !== list.length - 1">|</span>      </div>    </div>    <p class="text text1">      <span>旅游热线：86-28-83510033</span>      <span>爱心捐赠：86-28-83505513</span>      <span>失物招领、投诉电话：86-28-83507814</span>      <span>导游预约电话：86-28-83516748</span>      <span>救援电话：86-28-83516808</span>    </p>    <p class="text text2">      <span>传真：86-28-83516911</span>      <span>邮箱：pandabase@panda.org.cn</span>      <span>地址：中国四川省成都市成华区熊猫大道1375号</span>    </p>    <p class="text text3">      Copyright ? 2001-2023 panda.org.cn 成都大熊猫繁育研究基地版权所有      &nbsp;&nbsp;&nbsp;<span @click="detail(chuanGongWang)"><img src="https://file.daqsoft.com/uploadfile/server/geekui/panda/PC/wanbei.png" style="width: 18px" alt="" />川公网安备 51140202000125号</span>      &nbsp;&nbsp;<span @click="detail(beiAnHao)">备案号：蜀ICP备11021125号-3</span>    </p>    <div class="danwei-wrap">      <div class="danWei-btn">        <img src="https://file.daqsoft.com/uploadfile/server/geekui/panda/PC/danwei.png" alt="" @click="danweiClick"/>      </div>    </div>  </div></template><script>import { getNewsList, getIndexCityCardInfo } from '@/service/apiList/common'export default {  name: 'FooterCom',  inject: ['PCJumpDetails'],  data () {    return {      jumpList: [        {          imgUrl: 'https://file.daqsoft.com/uploadfile/server/geekui/panda/PC/weixin.png',          title: 'WEB',          mediaImage: ''          // mediaImage: 'https://file.daqsoft.com/uploadfile/server/wl-cloud/cultural-tourism-cloud/亢家寨公众号二维码.jpg'        },        {          imgUrl: 'https://file.daqsoft.com/uploadfile/server/geekui/panda/PC/weibo.png',          title: 'WEIBO',          mediaImage: ''          // mediaImage: 'https://file.daqsoft.com/uploadfile/server/wl-cloud/cultural-tourism-cloud/亢家寨公众号二维码.jpg'        },        {          imgUrl: 'https://file.daqsoft.com/uploadfile/server/geekui/panda/PC/douyin.png',          title: 'TIKTOK',          mediaImage: ''          // mediaImage: 'https://file.daqsoft.com/uploadfile/server/wl-cloud/cultural-tourism-cloud/亢家寨公众号二维码.jpg'        },        {          imgUrl: 'https://file.daqsoft.com/uploadfile/server/geekui/panda/PC/app.png',          title: '',          mediaImage: ''          // mediaImage: 'https://file.daqsoft.com/uploadfile/server/wl-cloud/cultural-tourism-cloud/亢家寨公众号二维码.jpg'        },      ],      list: [        {          name: '关于我们',          url: '/pc/about'        },        {          name: '联系我们',          url: '/pc/details?details=lxwm&channelCode=ykfw'        },        {          name: '游客服务',          url: '/pc/touristService'        },        {          name: '爱心支持',          url: '/pc/support'        },        {          name: '企业邮箱登陆',          url: 'http://mail.panda.org.cn/'        },      ],      chuanGongWang: {        url: 'http://www.beian.gov.cn/portal/registerSystemInfo?recordcode=51010802001155',        title: 'chuanGongWang'      },      beiAnHao: {        url: 'https://beian.miit.gov.cn/',        title: 'beiAnHao'      }    }  },  mounted() {    this.getCardInfo()  },  methods: {    detail (item) {      if (item.url) {        if (item.url.indexOf('http') !== -1) {          if(item.title === 'chuanGongWang' || item.title === 'beiAnHao') {            window.open(item.url)          } else {            location.href = item.url          }        } else {          if (item.url && item.url.includes('?details')) {            const params = this.getQueryString(item.url)            getNewsList({ channelCode: params.details }).then((res) => {              try {                this.PCJumpDetails(res.datas[0], params.channelCode)              } catch (error) {                this.$message({                  message: '敬请期待！',                  type: 'warning'                })              }            })            return ''          } else if(item.url && !item.url.includes('?details')) {            this.$router.push(item.url)          } else {            this.$message({              message: '敬请期待！',              type: 'warning'            })          }        }      } else {        this.$message({          message: '敬请期待',          type: 'warning'        })      }    },    getQueryString(url) {      let ItemArr = []      let ItemObj = {}      url        .substring(url.lastIndexOf('?') + 1, url.length)        .split('&')        .map((item) => {          ItemArr.push(item.split('='))        })      ItemArr.map((item) => {        ItemObj[item[0]] = item[1]      })      return ItemObj    },    getCardInfo () {      getIndexCityCardInfo({        cardType: 'CITY',        siteId: this.siteId      }).then(res => {        this.jumpList.map(item => {          res.data.mediaInfoList.map(item2 => {            if(item.title === item2.mediaType) {              Object.assign(item, item2)            }          })        })      })    },    danweiClick () {      window.open('https://bszs.conac.cn/sitename?method=show&id=97C3D4D49BF047DEE05310291AAC2F69')    }  }}</script><style lang="scss" scoped>.footer-wrap {  width: 100%;  height: 480px;  background: #000;  overflow: hidden;  z-index: 86;  position: relative;  .jump {    margin: 60px auto 40px;    text-align: center;    .jump-list {      display: inline-block;      margin: 0 60px;      cursor: pointer;      position: relative;      .qr-code {        width: 94%;        position: absolute;        bottom: 50%;        left: 50%;        transform: translate(-50%, -50%);        img {          width: 100%;        }      }    }  }  .relation {    text-align: center;    .relation-list {      display: inline-flex;      color: #ffffff;      font-size: 16px;      align-items: center;      .name {        cursor: pointer;        border-bottom: 1px solid transparent;        padding-bottom: 2px;        &:hover {          border-bottom-color: #ffffff;        }      }      .line {        font-weight: bold;        margin: 0 10px;      }    }  }  .text {    font-size: 16px;    text-align: center;    color: #999999;    white-space: pre-wrap;    span {      margin: 0 10px;    }  }  .text1 {    margin: 30px 0 19px  }  .text3 {    margin-top: 18px;    display: flex;    justify-content: center;    align-items: center;    border-bottom: 1px solid transparent;    padding-bottom: 2px;    span {      cursor: pointer;      display: inline-flex;      justify-content: center;      align-items: center;      border-bottom: 1px solid transparent;      padding-bottom: 2px;      &:hover {        border-bottom-color: #999999;      }    }  }  .danwei-wrap {    width: 100%;    text-align: center;    margin-top: 30px;    .danWei-btn {      img {        width: 56px;        cursor: pointer;      }    }  }}</style>