<template>  <div class="banner" :style="{'background-image': `url(${getUrl(banner.backgroundImg1)})`, 'background-repeat': 'no-repeat', 'background-position-y': '50%', 'background-size': 'cover'}">    <div class="title">      <p class="">{{ banner.name }}</p>      <i class="">{{ banner.summary }}</i>      <div class="banner-down-btn">        <img src="https://file.daqsoft.com/uploadfile/server/geek-move/geekerUI-mobile/down-btn.png" alt="" />      </div>    </div>  </div></template><script>import { getChannelDetail } from '@/service/apiList/common'export default {  name: 'PCBanner',  props: {    bannerText: {      type: Object,      defaule() {        return {}      }    },    channelCode: {      type: String,      default: '',    },  },  data() {    return {      banner: {},    }  },  computed: {    getUrl() {      return (url) => {        if (url) {          return url        }        return this.$Placeholder      }    },  },  created() {    this.getChannelDetail()  },  methods: {    async getChannelDetail() {      const res = await getChannelDetail({        channelCode: this.channelCode,      })      if (res.code === 0) {        this.banner = res.data      }    },  },  watch: {    channelCode: {      handler(to) {        if (to) {          this.getChannelDetail()        }      },      immediate: true,      deep: true,    }  }}</script><style lang="scss" scoped>.banner {  width: 100%;  height: 760px;  position: relative;  .title {    position: absolute;    width: 100%;    left: 0;    bottom: 0;    text-align: center;    p {      font-size: 40px;      color: #ffffff;      font-weight: bold;    }    i {      display: block;      font-size: 30px;      margin-top: 40px;      color: #ffffff;    }    .banner-down-btn {      text-align: center;      margin: 119px auto 40px;      -webkit-animation: bounce-down 1.6s linear infinite;      animation: bounce-down 1.6s linear infinite;      img {        width: auto;      }    }  }  @keyframes bounce-down {    25% {      transform: translateY(-4px);    }    50%, 100% {      transform: translateY(0);    }    75% {      transform: translateY(4px);    }  }}</style>