<template>  <transition enter-active-class="animation-fade-in" leave-active-class="animation-fade-out">    <div class="daq-toast" v-if="show" :style="boxStyle" :class="{ isloading: type === 'loading' }">      <div class="daq-toast-wrap" :class="[icon.className, position]" v-if="type !== 'loading'">        <span class="icon cloud-icon" v-if="icon.html && type !== 'loading'" v-html="icon.html"></span>        <span class="text" v-if="icon.title">{{ icon.title }}</span>      </div>    </div>  </transition></template><script>export default {  name: 'daq-toast',  data() {    return {}  },  props: {    boxStyle: {      type: Object,      default: () => {        return {}      },    },    show: Boolean,    position: {      type: String,      defult: 'top',      validator: (value) => {        return ['top', 'center', 'bottom'].indexOf(value) !== -1      },    },    type: {      type: String,      default: 'html',      validator: (value) => {        return ['html', 'loading', 'sucess', 'error'].indexOf(value) !== -1      },    },    message: String,  },  computed: {    icon() {      let html = ''      let title = ''      let className = ''      /* eslint-disable */      switch (this.type) {        case 'loading':          html = '&#xe6c9;'          title = this.message          className = 'loading'          break        case 'html':          html = ''          title = this.message          className = ''          break        case 'sucess':          html = '&#xe6b6;'          title = this.message          className = 'sucess'          break        case 'error':          html = '&#xe656;'          title = this.message          className = 'error'          break      }      /* eslint-disable */      return {        html: html,        title: title,        className: className,      }    },  },}</script><style lang="scss" scoped>.daq-toast-wrap .text {  padding: 0.2rem;}.loading-icon__background {  fill: #f5f5f5;}.loading-icon__animated-stroke,.loading-icon__static-stroke-section {  stroke: #36cd64;}.daq-loading-wrap {  position: relative;  width: 1.2rem;  height: 1.2rem;  justify-content: center;  align-items: center;  border-radius: 0.1rem;  .cloud-icon {    display: block;    position: absolute;    left: 0;    top: 0;    width: 100%;    height: 100%;    color: #ccc;    line-height: 1.2rem;    text-align: center;    font-size: 0.56rem;  }  .loading-icon {    position: absolute;    width: 1.2rem;    height: 1.2rem;    left: 50%;    top: 50%;    transform: translate(-50%, -50%);    overflow: visible;    .loading-icon__animated-stroke {      stroke-dasharray: 627.3;      animation: loading-icon-stroke 3.5s ease-in-out infinite, loading-icon-spin 3s linear infinite;    }    .loading-icon__static-stroke-section {      stroke-dasharray: 50 577.3;      stroke-dashoffset: 25;      animation: loading-icon-spin 3s linear infinite;    }    text {      font-family: sans-serif;      font-size: 25px;    }  }  @keyframes loading-icon-stroke {    0% {      stroke-dashoffset: 602.3;    }    100% {      stroke-dashoffset: -602.3;    }  }  @keyframes loading-icon-spin {    0% {      transform: rotate(0deg);    }    100% {      transform: rotate(360deg);    }  }}/*loading  end*/.daq-toast {  position: fixed;  left: 0;  right: 0;  bottom: 0;  top: 0;  z-index: 1000;  text-align: center;  &.isloading {    display: flex;    justify-content: center;    align-items: center;  }  &-mask {    position: absolute;    left: 0;    right: 0;    bottom: 0;    top: 0;    z-index: 1;    /*background: rgba(0, 0, 0, .4);*/  }  &-wrap {    display: inline-block;    color: #fff;    position: relative;    z-index: 2;    padding: 0.1rem 0.2rem;    min-width: 2.4rem;    width: auto;    text-align: center;    background: rgba(0, 0, 0, 0.7);    border-radius: 0.1rem;    overflow: hidden;    margin: 20% auto 0;  }  .top {    margin: 20% auto 0;  }  .center {    margin: 100% auto 0;  }  .bottom {    margin: 180% auto 0;  }  .text {    display: block;    font-size: 0.28rem;    line-height: 1.2;  }  .icon {    display: inline-block;    width: 1rem;    height: 1rem;    line-height: 1rem;    text-align: center;    border-radius: 50%;    font-size: 0.7rem;  }  .loading,  .sucess,  .error {    padding: 0.4rem 0;  }}</style>