<template>  <div class="banner-home">    <div class="title">      <p>Welcome to Chengdu</p>      <p>China Charm・Panda Cradle</p>    </div>    <div class="text">      <p>The land of abundance, the hometown of pandas</p>      <p>The natural paradise of national treasures, our paradise</p>    </div>    <!-- 图标 -->    <div class="icon">      <MyIcon name="xiala" size="0.7rem" color="#fff" />    </div>    <!-- 轮播 -->    <div class="shuffling">      <div class="title">Panda Ambassador</div>      <swiper class="my-swiper" data-wow-duration="2s" :options="swiperOption" ref="mySwiper">        <swiper-slide v-for="item in bannerList" :key="item.id">          <div class="box">            <div class="name">{{ item.title }}</div>            <div class="shadow"></div>            <img :src="getUrl(item)" alt="" />          </div>        </swiper-slide>        <template v-slot:pagination >          <div v-show="bannerList.length" class="swiper-pagination" style="bottom: 0.16rem"></div>        </template>      </swiper>    </div>  </div></template><script>import { getSimplifyList } from '@/service/apiList/common'export default {  name: 'BannerHome',  data() {    return {      swiperOption: {        slidesPerView: 'auto',        loop: false,        autoplay: {          delay: 4000,          disableOnInteraction: false,        },        // 显示分页        pagination: {          el: '.swiper-pagination',        },      },      bannerList: [],    }  },  created() {    this.getImg()  },  computed: {    getUrl() {      return (item) => {        try {          return item.imageUrls[0].url        } catch (error) {          return this.$Placeholder        }      }    },  },  methods: {    async getImg() {      const res = await getSimplifyList({        channelCode: 'xmds',      })      if (res.code === 0) {        this.bannerList = res.datas      }    },  },}</script><style lang="scss" scoped>.banner-home {  background: url('https://file.daqsoft.com/uploadfile/server/geekui/panda/H5/en/未命名_副本.png') no-repeat;  width: 7.5rem;  height: 10rem;  background-size: 100%;  > .title {    padding-top: 1.1rem;    margin-left: 0.4rem;    font-weight: 700;    font-size: 0.5rem;    color: #fff;    p {      line-height: 1;      &:last-child {        margin-top: 0.23rem;      }    }  }  .text {    margin-left: 0.4rem;    margin-top: 0.34rem;    font-weight: 400;    font-size: 0.24rem;    color: #fff;    line-height: 1;    p {      &:last-child {        margin-top: 0.23rem;      }    }  }  .icon {    margin-top: 0.36rem;    text-align: center;    animation: test 1000ms infinite;    -webkit-animation: test 1000ms infinite;    @keyframes test {      from {        opacity: 1;      }      50% {        opacity: 0.2;      }      to {        opacity: 0.8;      }    }  }  .shuffling {    margin: 1.52rem auto 0;    width: 5rem;    height: 3.26rem;    .title {      text-align: center;      line-height: 1;      font-weight: 700;      font-size: 0.3rem;      color: #fff;      margin-bottom: 0.23rem;    }    .my-swiper {      width: 5rem;      height: 2.8rem;      .box {        width: 5rem;        height: 2.8rem;        background: rgba(255, 255, 255, 0.4);        position: relative;        .name {          line-height: 1;          font-size: 0.24rem;          color: #fff;          position: absolute;          bottom: 0.23rem;          left: 0.23rem;          z-index: 10;        }        .shadow {          position: absolute;          bottom: 0;          left: 0;          width: 5rem;          height: 2.8rem;          background: linear-gradient(rgba(255, 255, 255, 0) 50%, rgba(0, 0, 0, 0.4));        }        img {          width: 5rem;          height: 2.8rem;        }      }    }  }  .swiper-pagination {    text-align: right;    padding-right: 0.24rem;    padding-bottom: 0.1rem;  }  ::v-deep .swiper-pagination-bullet-active {    width: 0.1rem !important;    height: 0.1rem !important;    opacity: 1 !important;    background: #fff !important;  }  ::v-deep .swiper-pagination-bullet {    width: 0.06rem;    height: 0.06rem;    opacity: 0.5;    margin-left: 0.06rem;    background: #ffffff;  }}</style>