<template>  <div class="news-events">    <div class="tab">      <div @click="$router.push(item.url)" class="item" v-for="item in tab" :key="item.id">        {{ item.name }}      </div>    </div>    <div class="title">News&Events</div>    <div class="list" v-if="list.length">      <div class="item" @click="jumpDetails(item)" v-for="item in list" :key="item.id">        <div class="title">{{ item.title }}</div>        <div class="text">{{ item.summary }}</div>      </div>    </div>    <div @click="$router.push('/h5/list?channelCode=NewsCenter&tab=News1')" class="button">News center <MyIcon name="xiala2-copy" size="0.3rem" color="#fff" /></div>  </div></template><script>import { getChannelSubset, getSimplifyList } from '@/service/apiList/common'export default {  name: 'NewsEvents',  data() {    return {      tab: [],      list: [],    }  },  created() {    this.getSubset()    this.getList()  },  methods: {    async getSubset() {      const res = await getChannelSubset({        channelCode: 'PandaWorld',      })      if (res.code === 0) {        this.tab = res.data.subset.filter((item) => {          return item.channelCode !== 'Activities'        })      }    },    async getList() {      const res = await getSimplifyList({        channelCode: 'News1',        pageSize: 2,        currPage: 1,      })      if (res.code === 0) {        this.list = res.datas      }    },    // Ìø×ªÏêÇé    jumpDetails(item) {      this.$router.push({        path: '/h5/details',        query: {          channelCode: 'NewsCenter',          id: item.id,          tab: 'News1',        },      })    },  },}</script><style lang="scss" scoped>.news-events {  background: url('https://file.daqsoft.com/uploadfile/server/geekui/panda/H5/en/±³¾°3.png') no-repeat;  background-size: 100%;  padding: 0.8rem 0.4rem;  width: 7.5rem;  height: 13.34rem;  .tab {    display: flex;    flex-wrap: wrap;    .item {      font-size: 0.26rem;      color: #fff;      width: 50%;      position: relative;      padding-left: 0.25rem;      &:after {        position: absolute;        left: 0;        top: 50%;        transform: translateY(-50%);        content: '';        width: 0.06rem;        height: 0.06rem;        background: #fff;      }      &:nth-child(n + 3) {        margin-top: 0.5rem;      }    }  }  > .title {    font-weight: 700;    font-size: 0.9rem;    margin-top: 0.6rem;    line-height: 1;    color: #fff;  }  > .list {    margin-top: 0.56rem;    .item {      margin-top: 0.3rem;      padding-bottom: 0.3rem;      border-bottom: 0.01rem solid #ffffff;      .title {        font-size: 0.36rem;        color: #fff;        line-height: 0.48rem;        @include ellipsisLn(2);        position: relative;        padding-left: 0.2rem;        &:after {          position: absolute;          left: 0;          top: 0.2rem;          content: '';          width: 0.06rem;          height: 0.06rem;          background: #fff;        }      }      .text {        font-size: 0.22rem;        color: rgba(255, 255, 255, 0.6);        line-height: 0.36rem;        margin-top: 0.02rem;        padding-left: 0.2rem;        @include ellipsisLn(2);      }    }  }  > .button {    margin: 0.4rem 0 0 0.26rem;    color: #fff;    font-size: 0.28rem;    height: 0.76rem;    padding: 0 0.44rem;    width: 3rem;    border: 0.02rem solid #fff;    display: flex;    justify-content: space-between;    align-items: center;  }}</style>