<template>  <div class="introduction">    <div class="title">基地简介</div>    <div class="num-box">      <div class="item">        <p class="num">1987<span>年</span></p>        <p class="text">基地成立</p>      </div>      <div class="item">        <p class="num">3570<span>亩</span></p>        <p class="text">占地面积</p>      </div>      <div class="item">        <p class="num">215<span>只</span></p>        <p class="text">大熊猫种群数量</p>      </div>    </div>    <div class="content">      <p v-if="details.summary" v-html="getText(details.summary)"></p>    </div>    <div class="example-3d" v-if="imgList.length">      <swiper ref="swiper" class="swiper" :options="swiperOption">        <swiper-slide v-for="item in imgList" :key="item.id">          <img :src="item.url || $Placeholder" alt="" />        </swiper-slide>      </swiper>    </div>    <MoreAndMore :path="'/h5/details?id=' + item.id + '&channelCode=gywm'" />    <!-- <div class="button"  @click="jumpDetails(item, 'gywm')">查看更多</div> -->  </div></template><script>import { getChannelDetail, getNewsList } from '@/service/apiList/common'import MoreAndMore from '@/views/H5/components/MoreAndMore.vue'export default {  name: 'IntroDuction',  inject: ['jumpDetails'],  components: {    MoreAndMore,  },  data() {    let fontSize = 50 * (window.outerWidth / 750)    return {      imgList: [],      details: {},      item: {},      swiperOption: {        effect: 'coverflow',        grabCursor: true,        initialSlide: 2,        centeredSlides: true,        slidesPerView: 'auto',        coverflowEffect: {          rotate: 0,          stretch: fontSize * 3.5,          depth: 220,          modifier: 1,        },      },    }  },  computed: {    getText() {      return (text) => {        if (text && text.length > 33) {          return `<span>${text.substring(0, 28)}</span>          <span>${text.substring(28, text.length)}</span>          `        }        return `<span>${text}</span>          `      }    },  },  created() {    this.getChannelDetail()    this.getNewsList()  },  methods: {    async getChannelDetail() {      const { code, data } = await getChannelDetail({ channelCode: 'jdjj' })      if (code === 0) {        this.details = data      }    },    async getNewsList() {      const res = await getNewsList({ channelCode: 'jdjj' })      if (res.code === 0) {        try {          this.imgList = res.datas[0].imageUrls          this.item = res.datas[0]        } catch (error) {          this.imgList = []        }      }    },  },}</script><style lang="scss" scoped>.example-3d {  width: 100%;  height: 3rem;  padding: 0 0.3rem;}.swiper {  height: 100%;  width: 100%;  ::v-deep .swiper-wrapper {    display: flex;    align-items: center;  }  .swiper-slide-active {    width: 3.3rem !important;    height: 2.2rem !important;  }  .swiper-slide {    display: flex;    justify-content: center;    align-items: center;    width: 3.3rem;    height: 1.8rem;    position: relative;    &::after {      content: '';      width: 2.37rem;      height: 0;      border-radius: 40%;      position: absolute;      bottom: -0.2rem;      box-shadow: 0px 0px 22px 3px #cfcfcf;    }    img {      height: 100%;    }  }}.introduction {  padding-bottom: 0.79rem;  > .title {    font-weight: Bold;    font-size: 0.46rem;    color: #333;    line-height: 1;    padding: 0.79rem 0 0.5rem;    text-align: center;  }  > .num-box {    display: flex;    justify-content: space-around;    text-align: center;    .num {      font-weight: Bold;      font-size: 0.6rem;      line-height: 1;      color: #15a98d;      span {        font-weight: 500;        font-size: 0.2rem;        margin-left: 0.08rem;        color: #666666;      }    }    .text {      margin-top: 0.16rem;      font-weight: 500;      font-size: 0.2rem;      line-height: 1;      color: #666666;    }  }  > .content {    margin: 0.5rem 0 0.2rem;    p {      font-size: 0.24rem !important;      color: #333333 !important;      padding: 0 0.3rem;      line-height: 0.36rem !important;      ::v-deep span {        text-align: center;        display: block;        &:nth-child(2) {          width: 5rem;          @include ellipsis(5rem);          margin: auto;        }      }    }  }  > img {    width: 100%;  }  // > .button {  //   margin: 0.4rem auto 0.79rem;  //   width: 2rem;  //   height: 0.6rem;  //   color: #fff;  //   line-height: 0.6rem;  //   font-size: 0.24rem;  //   background: #15a98d;  // }}</style>