e: 100%;  > .title {    text-align: center;    font-weight: Bold;    font-size: 0.46rem;    color: #ffffff;    padding: 0.4rem 0 0.5rem;  }  .box {    width: 6.9rem;    height: 5.4rem;    background: rgba(255, 255, 255, 0.9);    overflow: hidden;    .my-swiper {      width: 6.5rem;      margin: 0.2rem;      .banner {        width: 100%;        height: 3.48rem;        margin-bottom: 0.38rem;      }      .title {        font-weight: Bold;        line-height: 1;        font-size: 0.26rem;        color: #333333;        @include ellipsis();      }      .text {        text-indent: 0.48rem;        margin-top: 0.2rem;        font-size: 0.24rem;        color: #666666;        line-height: 0.36rem;        @include ellipsisLn(2);      }    }  }  .swiper-pagination {    display: flex;    align-items: center;    justify-content: center;    bottom: 1.8rem !important;    ::v-deep .swiper-pagination-bullet {      width: 0.12rem;      height: 0.12rem;      border: 0.01rem solid #ffffff;      background: none;      opacity: 1;      margin: 0.08rem;    }    ::v-deep .swiper-pagination-bullet-active {      width: 0.2rem;      height: 0.2rem;      border: 0.05rem solid #ffffff;    }  }}</style>