<template>  <div>    <div class="announ-ccement" v-if="list && list.length">      <TitleBox title="单位公告" path="/h5/baseDynamic/list?channelCode=dwgg" />      <div class="top-title" @click="jumpDetails(list[0], 'jddt')">        <div class="title">{{ list[0].title }}</div>        <div class="info" v-if="list[0].summary">          {{ list[0].summary }}        </div>      </div>      <ul>        <li @click="jumpDetails(item, 'jddt')" v-show="index !== 0" v-for="(item, index) in list" :key="item.id">{{ item.title }}</li>      </ul>    </div>    <TextSkeleton v-else />  </div></template><script>import { getNewsList } from '@/service/apiList/common'import TitleBox from '@/views/H5/index/components/TitleBox.vue'import TextSkeleton from '@/components/h5Skeleton/TextSkeleton.vue'export default {  name: 'AnnounCement',  inject: ['jumpDetails'],  components: {    TitleBox,    TextSkeleton,  },  data() {    return {      list: [],    }  },  created() {    this.getNewsList()  },  methods: {    // 熊猫大使头像    async getNewsList() {      const res = await getNewsList({        channelCode: 'dwgg',        pageSize: 5,        currPage: 1,      })      if (res.code === 0) {        this.list = res.datas      }    },  },}</script><style lang="scss" scoped>.announ-ccement {  background: #f5f5f5;  > .top-title {    margin: 0 0.32rem;    border-bottom: 0.01rem solid #dedede;    padding-bottom: 0.4rem;    > .title {      font-weight: Bold;      font-size: 0.26rem;      color: #15a98d;      @include ellipsis(100%);    }    > .info {      margin-top: 0.3rem;      color: #999999;      font-size: 0.24rem;      line-height: 0.36rem;      @include ellipsisLn(2);    }  }  > ul {    padding: 0.4rem 0.32rem 0.79rem;    li {      font-weight: Bold;      font-size: 0.26rem;      color: #666666;      padding: 0.1rem;      position: relative;      padding-left: 0.3rem;      &::after {        position: absolute;        left: 0;        top: 0.18rem;        content: '';        width: 0.14rem;        height: 0.14rem;        border-radius: 50%;        border: 0.01rem solid #666666;      }      @include ellipsis(100%);      &:hover {        color: #15a98d;      }    }  }}</style>