<template>  <div class="focus-of" v-if="list && list.length">    <TitleBox title="焦点活动" color="#FFFFFF" path="/h5/baseDynamic/list?channelCode=jdhd" />    <swiper class="my-swiper" :options="swiperOption" ref="mySwiper">      <swiper-slide v-for="item in list" :key="item.id">        <div class="box">          <img :src="item.imageUrls.length && item.imageUrls[0].url || $Placeholder" alt="" />        </div>      </swiper-slide>      <template v-slot:pagination>        <div v-show="list.length > 1" class="swiper-pagination" style="bottom: 0.16rem"></div>      </template>    </swiper>  </div></template><script>import { getNewsList } from '@/service/apiList/common'import TitleBox from '@/views/H5/index/components/TitleBox.vue'export default {  name: 'FocusOf',  inject:['jumpDetails'],  components: {    TitleBox,  },  data() {    const _this = this    return {      swiperOption: {        slidesPerView: 'auto',        loop: false,        autoplay: {          delay: 4000,          disableOnInteraction: false        },        // 显示分页        pagination: {          el: '.swiper-pagination',        },        on: {          click: function() {            _this.jumpDetails(_this.list[this.realIndex],'jddt')          },        },      },      list: [],    }  },  created() {    this.getNewsList()  },  methods: {    async getNewsList() {      const res = await getNewsList({        channelCode: 'jdhd',      })      if (res.code === 0) {        this.list = res.datas      }    },  },}</script><style lang="scss" scoped>.focus-of {  background: url('https://file.daqsoft.com/uploadfile/server/geekui/panda/H5/焦点活动.png') no-repeat;  background-size: 100%;  width: 7.5rem;  height: 7.2rem;  .my-swiper {    width: 6.9rem;    height: 4.3rem;    .box {      width: 6.9rem;      height: 3.83rem;      padding: 0.1rem;      background: rgba(255, 255, 255, 0.4);      img {        width: 6.7rem;        height: 3.63rem;      }    }  }  .swiper-pagination {    display: flex;    align-items: center;    justify-content: center;    bottom: -0.05rem !important;    ::v-deep .swiper-pagination-bullet {      width: 0.12rem;      height: 0.12rem;      border: 0.01rem solid #ffffff;      background: none;      opacity: 1;      margin: 0.08rem;    }    ::v-deep .swiper-pagination-bullet-active {      width: 0.2rem;      height: 0.2rem;      border: 0.05rem solid #ffffff;    }  }}</style>