<template>  <div class="gallery">    <div class="title" @click="$router.push('/h5/generalList?channelCode=xmyst&banner=xmwh')">–‹√®“’ ıÕ≈</div>    <div class="box" v-if="list.length">      <swiper class="swiper" :options="swiperOption">        <swiper-slide v-for="item in list" :key="item.id">          <div class="content" @click.stop="jumpDetails(list[index], 'xmwh')">            <img :src="(item.imageUrls.length && item.imageUrls[0].url) || $Placeholder" alt="" />          </div>        </swiper-slide>        <template v-slot:button-prev>          <div class="swiper-button-prev"></div>        </template>        <template v-slot:button-next>          <div class="swiper-button-next"></div>        </template>      </swiper>    </div>    <div class="name" v-if="list.length">{{ list[list.length === 1 ? 0 : index].title }}</div>    <div class="text" v-if="list.length">{{ list[list.length === 1 ? 0 : index].summary }}</div>  </div></template><script>import { getNewsList } from '@/service/apiList/common'export default {  name: 'GalleryIndex',  inject: ['jumpDetails'],  data() {    const _this = this    return {      list: [],      index: 1,      swiperOption: {        slidesPerView: 3,        initialSlide: 1,        centeredSlides: true,        spaceBetween: 0,        navigation: {          nextEl: '.swiper-button-next',          prevEl: '.swiper-button-prev',        },        on: {          slideChange: function () {            _this.index = this.activeIndex          },        },      },    }  },  created() {    this.getNewsList()  },  methods: {    async getNewsList() {      const res = await getNewsList({ channelCode: 'xmyst' })      if (res.code === 0) {        this.list = res.datas      }    },  },}</script><style lang="scss" scoped>.gallery {  text-align: center;  padding: 0.79rem 0;  background: rgb(255, 255, 255);  > .title {    font-weight: Bold;    font-size: 0.46rem;    color: #333333;    line-height: 1;  }  ::v-deep .swiper-wrapper {    display: flex;    align-items: center;  }  .box {    width: 7rem;    margin: auto;  }  .swiper {    height: 3rem;    padding: 0.5rem 0 0;    .swiper-slide {      display: flex;      justify-content: center;      align-items: center;      .content {        width: 1.2rem;        height: 1.2rem;        overflow: hidden;        border-radius: 50%;        img {          width: 100%;          height: 100%;          object-fit: cover;        }      }    }    .swiper-slide-active {      width: 2.4rem !important;      .content {        height: 2.4rem;        width: 2.4rem !important;        display: flex;        align-items: center;        justify-content: center;        border-radius: 50%;        box-shadow: 0 0 0.1rem 0 #ccc;        background: #fff;        img {          width: 1.98rem;          height: 1.98rem;          border-radius: 50%;        }      }    }    .swiper-button-prev {      margin-top: 0;      filter: brightness(0);      opacity: 40%;      background-size: 0.27rem 0.44rem;      width: 0.27rem;      height: 0.44rem;    }    .swiper-button-next {      margin-top: 0;      filter: brightness(0);      opacity: 40%;      width: 0.27rem;      height: 0.44rem;      background-size: 0.27rem 0.44rem;    }    .content {      background: rgba(203, 203, 203, 1);    }  }  .name {    margin-top: 0.4rem;    line-height: 1;    font-weight: Bold;    font-size: 0.26rem;    color: #333333;  }  .text {    margin-top: 0.19rem;    line-height: 1;    padding: 0 0.58rem;    font-weight: 500;    font-size: 0.24rem;    color: #666666;    @include ellipsis();  }}</style>