<template>  <div class="panda-activity">    <div class="title">–‹√®ªÓ∂Ø</div>    <img @click="jumpDetails(list[0], 'xmwh')" :src="getUrl(list)" alt="" />    <div>      <div class="item" @click="jumpDetails(item, 'xmwh')" v-for="item in list" :key="item.id">        <p class="title">{{ item.title }}</p>        <p class="text" v-if="item.summary">{{ item.summary }}</p>        <p class="time">{{ item.publishTime }}</p>      </div>    </div>    <MoreAndMore path="/h5/generalList?channelCode=xmhd&banner=xmwh" />  </div></template><script>import { getNewsList } from '@/service/apiList/common'import MoreAndMore from '@/views/H5/components/MoreAndMore.vue'export default {  name: 'PandaActivity',  inject: ['jumpDetails'],  components: {    MoreAndMore,  },  data() {    return {      list: [],    }  },  created() {    this.getNewsList()  },  computed: {    getUrl() {      return (list) => {        try {          return list[0].imageUrls[0].url        } catch (error) {          return this.$Placeholder        }      }    },  },  methods: {    async getNewsList() {      const res = await getNewsList({        channelCode: 'xmhd',        pageSize: 4,        currPage: 1,      })      if (res.code === 0) {        if (res.datas.length) {          this.list = res.datas        }      }    },  },}</script><style lang="scss" scoped>.panda-activity {  padding: 0 0.3rem 0.4rem;  > .title {    font-weight: Bold;    font-size: 0.46rem;    color: #333333;    line-height: 1;    padding: 0.79rem 0 0.5rem;    text-align: center;  }  img {    width: 6.9rem;    height: 3.83rem;  }  .item {    max-height: 2.43rem;    padding: 0.4rem 0.21rem 0.44rem;    background: rgba(245, 245, 245, 1);    margin-bottom: 0.2rem;    &:last-child {      margin: 0;    }    > .title {      font-weight: Bold;      font-size: 0.26rem;      color: #333333;      line-height: 1;      @include ellipsis();    }    > .text {      text-indent: 0.48rem;      margin-top: 0.29rem;      font-size: 0.24rem;      color: #666666;      line-height: 0.36rem;      @include ellipsisLn(2);    }    > .time {      margin-top: 0.29rem;      font-weight: 500;      font-size: 0.2rem;      color: #999999;    }  }}</style>