ait getChannelDetail({        channelCode: 'xsjl',      })      if (res.code === 0) {        this.details = res.data      }    },  },}</script><style lang="scss" scoped>.academic {  padding: 0 0.3rem 0.4rem;  > .title {    padding: 0.79rem 0 0.5rem;    font-weight: Bold;    font-size: 0.46rem;    text-align: center;  }  p {    font-size: 0.24rem;    color: #333333;    line-height: 0.36rem;    margin-bottom: 0.5rem;    ::v-deep span {      text-align: center;      display: block;      &:nth-child(2) {        width: 5rem;        @include ellipsis(5rem);        margin: auto;      }    }  }  img {    width: 100%;  }  .button {    font-weight: 500;    font-size: 0.24rem;    padding: 0.18rem 0;    color: #fff;    margin: auto;    line-height: 1;    width: 2rem;    margin-top: 0.4rem;    background: #15a98d;    margin-bottom: 0.79rem;    text-align: center;  }  .list {    margin-bottom: 0.79rem;    > .item {      margin-bottom: 0.4rem;      width: 6.9rem;      max-height: 6.26rem;      background: rgba(245, 245, 245, 1);      > img {        width: 6.9rem;        height: 3.83rem;      }      > .title {        font-weight: Bold;        font-size: 0.26rem;        color: #333333;        line-height: 1;        padding: 0.4rem 0.2rem 0.28rem;        @include ellipsis();      }      > .text {        font-size: 0.24rem;        color: #666666;        line-height: 0.36rem;        padding: 0 0.2rem;        @include ellipsisLn(2);      }      > .time {        line-height: 1;        padding: 0.26rem 0.2rem 0.44rem;        font-weight: 500;        font-size: 0.2rem;        color: #999999;      }    }  }}</style>