<template>  <div class="navigation-map">    <div class="title">导览地图</div>    <div class="left" @click="jump('dldt')"></div>    <div class="right" @click="jump('cygw')"></div>  </div></template><script>import { getNewsList } from '@/service/apiList/common'export default {  name: 'NavigationMap',  inject: ['jumpDetails'],  methods: {    jump(text) {      getNewsList({        channelCode: text,      }).then((res) => {        if (res.code === 0) {          try {            this.jumpDetails(res.datas[0], 'ykfw')          } catch (error) {            return ''          }        }      })    },  },}</script><style lang="scss" scoped>.navigation-map {  width: 7.5rem;  height: 12rem;  background: url('https://file.daqsoft.com/uploadfile/server/geekui/panda/H5/导览地图.png') no-repeat;  background-size: 100%;  position: relative;  .title {    font-weight: Bold;    font-size: 0.46rem;    padding: 1rem 0.5rem;    line-height: 1;    color: #fff;    text-align: center;  }  .left {    position: absolute;    left: 0.95rem;    top: 1.92rem;    width: 2.6rem;    height: 0.63rem;  }  .right {    position: absolute;    right: 0.95rem;    top: 1.92rem;    width: 2.6rem;    height: 0.63rem;  }}</style>