<template>  <div class="ticketing">    <div class="title" @click="jump('pwfw')">票务服务</div>    <p class="text">景区已经全面实现智慧化，提前购买门票或预约服务，可避免在 高峰期间的排队困扰。</p>    <img src="https://file.daqsoft.com/uploadfile/server/geekui/panda/H5/二维码.png" alt="" />    <p class="amount">全价门票：55元/人，半价门票：27元/人</p>    <p class="amount">打开微信扫一扫，扫码立即预订。</p>  </div></template><script>import { getNewsList } from '@/service/apiList/common'export default {  name: 'TickeTing',  inject: ['jumpDetails'],  methods: {    jump(text) {      getNewsList({        channelCode: text,      }).then((res) => {        if (res.code === 0) {          try {            this.jumpDetails(res.datas[0], 'ykfw')          } catch (error) {            return ''          }        }      })    },  },}</script><style lang="scss" scoped>.ticketing {  background: url('https://file.daqsoft.com/uploadfile/server/geekui/panda/H5/票务服务bg.png') no-repeat;  background-size: 100%;  width: 7.5rem;  height: 7.2rem;  padding: 0 0.4rem;  text-align: center;  .title {    font-weight: Bold;    font-size: 0.46rem;    color: #ffffff;    line-height: 1;    padding: 0.79rem 0 0.4rem;  }  .text {    font-size: 0.24rem;    line-height: 0.36rem;    color: #ffffff;  }  img {    width: 2.2rem;    margin: 0.6rem 0;  }  .amount {    line-height: 1;    margin-bottom: 0.23rem;    font-size: 0.24rem;    color: #fff;  }}</style>