<template>  <div class="section-three">    <div class="left wow fadeInDown" @click="objClick(leftObj)">      <div class="img" :style="{'background-image': 'url(' + getUrl('https://file.daqsoft.com/uploadfile/server/geekui/panda/Japanese/img05.jpg') + ')','background-size': 'cover','background-repeat': 'no-repeat',}"></div>      <img src="https://file.daqsoft.com/uploadfile/server/geekui/panda/Japanese/img05_1668419224112.png" alt="" class="flowers" />      <p class="title">{{ leftObj.channelCode }}</p>      <p class="name">{{ strIntercept(leftObj.name) }}</p>    </div>    <div class="right wow fadeInDown" @click="objClick(rightObj)">      <div class="img" :style="{'background-image': 'url(' + getUrl('https://file.daqsoft.com/uploadfile/server/geekui/panda/Japanese/img06.jpg') + ')','background-size': 'cover','background-repeat': 'no-repeat',}"></div>      <img src="https://file.daqsoft.com/uploadfile/server/geekui/panda/Japanese/img06_1668418993460.png" alt="" class="flowers" />      <p class="title">{{ rightObj.channelCode }}</p>      <p class="name">{{ strIntercept(rightObj.name) }}</p>    </div>  </div></template><script>import { getChannelDetail, channelcrumbs } from '@/service/apiList/common'export default {  name: 'SectionThree',  data() {    return {      leftObj: {},      rightObj: {}    }  },  computed: {    getUrl() {      return (url) => {        if (url) {          return url        }        return this.$Placeholder      }    },    strIntercept() {      return (item) => {        try {          return item.substring(0, item.indexOf('ги'))        } catch (error) {          return item        }      }    }  },  mounted() {    this.getChannelDetail1()    this.getChannelDetail2()  },  methods: {    async getChannelDetail1 () {      const res = await getChannelDetail({ channelCode: 'research' })      if (res.code === 0) {        this.leftObj = res.data      }    },    async getChannelDetail2 () {      const res = await getChannelDetail({ channelCode: 'education' })      if (res.code === 0) {        this.rightObj = res.data      }    },    objClick(item) {      channelcrumbs({        channelCode: item.channelCode      }).then(res => {        if(res.code === 0) {          this.$router.push(res.data.url1)        }      })    }  }}</script><style lang="scss" scoped>.section-three {  display: flex;  .left, .right {    width: 50%;    display: inline-block;    position: relative;    height: 6.4rem;    cursor: pointer;    overflow: hidden;    .img {      width: 100%;      height: 100%;      overflow: hidden;      transition: all 0.25s;    }    .flowers {      position: absolute;    }    .title {      font-family: 'Edwardian Script ITC';      text-align: center;      font-size: 2rem;      color: rgba(238, 238, 238, 0.6);      position: absolute;      bottom: 0.5rem;      left: 50%;      transform: translateX(-50%);    }    .name {      font-size: 0.32rem;      text-align: center;      color: #ffffff;      position: absolute;      bottom: 0.5rem;      left: 50%;      transform: translateX(-50%);      border-bottom: 2px solid transparent;    }    &:hover {      .img {        transform: scale(1.1);      }      .name {        border-bottom-color: rgba(255, 255, 255, 1);      }    }  }  .left {    .flowers {      top: 0;      left: 0;    }  }  .right {    .flowers {      bottom: 0;      right: 0;    }  }}</style>