<template>  <div>    <div class="time-wrap">      <p class="wow fadeInDown">_@rg：<span>07：30-18：00</span></p>      <p class="wow fadeInDown">_@rg：午前7r30分 ～午後6r、入@は午後5 rまでです</p>      <p class="wow fadeInDown">入@料金について：入@料金：55元／人，半~：27元／人</p>    </div>    <div class="main-wrap">      <div class="top">        <div class="left">          <div class="img">            <p class="title wow fadeIn">Service</p>            <p class="name wow fadeIn">ご利用案内</p>          </div>        </div>        <div class="line"></div>        <div class="right">          <p class="time wow fadeIn">&nbsp;</p>          <div class="right-list-wrap">            <div class="right-list-box wow fadeIn" v-for="(item, index) in rightList" :key="index" @click="jump(item)">              <MyIcon :name="item.icon" color="#999999" size="0.34rem"/>              <p>{{ item.name }}</p>            </div>          </div>        </div>      </div>      <div class="bottom">        <div class="picture-wrap">          <div class="big-picture wow bounceIn">            <img src="https://file.daqsoft.com/uploadfile/server/geekui/panda/PC/Japanese/index/img01.png" alt="" />          </div>          <div class="small-picture wow bounceIn" data-wow-delay="0.8s">            <img src="https://file.daqsoft.com/uploadfile/server/geekui/panda/PC/Japanese/index/img02.png" alt="" />          </div>        </div>        <div class="tab-wrap">          <div class="list-box wow fadeInLeft" :data-wow-delay="getDelay(index)" v-for="(item, index) in tabList" :key="index" :class="index < 4 ? 'class-one' : 'class-two'" @click="tabClick(item)">            <span>{{ strIntercept(item.name) }}</span>          </div>        </div>      </div>    </div>  </div></template><script>import { channelcrumbs } from '@/service/apiList/common'export default {  name: 'sectionTwo',  data () {    return {      tabList: [],      rightList: [        {          name: '@内カ`ト案内',          icon: 'jiaotong',        },        {          name: 'ガイドツア`',          icon: 'yule1',        },        {          name: '@内マップ',          icon: 'quanjing',        },        {          name: 'アクセス',          icon: 'luxian',        }      ]    }  },  computed: {    getDelay() {      return (index) => {        try {          return `${index / 5 + 1}s`        } catch (error) {          return '1s'        }      }    },    strIntercept() {      return (name) => {        try {          return name.includes('（') ? name.substring(0, name.indexOf('（')) : name        } catch (error) {          return name        }      }    }  },  mounted() {    this.getList()  },  methods: {    tabClick(item) {      this.$router.push(item.url1)    },    getList () {      channelcrumbs({        channelCode: 'pandaCulture'      }).then(res => {        if(res.code === 0) {          this.tabList = res.data.subset        }      })    },    jump(item) {      channelcrumbs({        channelCode: 'touristService'      }).then(res => {        if(res.code === 0) {          res.data.subset.forEach(item2 => {            if(item2.name.includes(item.name)) {              this.$router.push(item2.url1)            }          })        }      })    }  }}</script><style lang="scss" scoped>.time-wrap {  width: 100%;  height: 2.8rem;  background-image: url("https://file.daqsoft.com/uploadfile/server/geekui/panda/PC/Japanese/index/bg2.png");  background-repeat: no-repeat;  background-size: cover;  background-position: center center;  position: relative;  overflow: hidden;  padding: .85rem 0;  text-align: center;  color: #333333;  font-size: .18rem;  p {    margin-bottom: .2rem;    span {      color: #15A98D;      display: inline-block;      border-bottom: 1px solid #15A98D;    }  }}.main-wrap {  width: 100%;  height: 16.04rem;  background-image: url("https://file.daqsoft.com/uploadfile/server/geekui/panda/PC/Japanese/index/bg3.png");  background-repeat: no-repeat;  background-size: contain;  background-position: center center;  position: relative;  overflow: hidden;  .top {    position: absolute;    width: 100%;    top: 0;    display: flex;    .left, .right {      width: calc(50% - 0.01rem);    }    .line {      width: 0.01rem;      height: 3.95rem;      display: inline-block;      background: #eeeeee;    }    .left {      padding: .6rem .8rem 0 0;      text-align: right;      .img {        display: inline-block;        width: 5.09rem;        text-align: center;        .title {          font-size: 2rem;          font-family: 'Edwardian Script ITC';          color: #eeeeee;          margin: -0.2rem 0 -0.2rem;        }        .name {          font-size: .32rem;        }      }    }    .right {      padding: 1.11rem 0 0 .8rem;      .title {        color: #15A98D;        font-size: .18rem;        font-weight: bold;      }      .time {        color: #333333;        font-size: .18rem;        margin: .24rem 0 .29rem;      }      .right-list-wrap {        display: flex;        .right-list-box {          text-align: center;          color: #666666;          font-size: .18rem;          cursor: pointer;          &:hover {            i, p {              color: #15A98D !important;            }          }          &:not(:last-child) {            margin-right: .5rem;          }          p {            margin-top: .17rem;          }        }      }    }  }  .bottom {    position: relative;    top: 3.5rem;    .picture-wrap {      position: absolute;      top: 0;      right: 1.8rem;      display: inline-block;      .big-picture {        width: 11.2rem;        height: 11.2rem;        border-radius: 50%;        border: 0.01rem solid #EEEEEE;        padding: .3rem;        background: #ffffff;        img {          width: 100%;        }      }      .small-picture {        position: absolute;        top: 0.32rem;        left: -0.09rem;        width: 2.89rem;        height: 2.89rem;        border-radius: 50%;        border: 1px solid #EEEEEE;        padding: .2rem;        background: transparent;        img {          width: 100%;        }      }    }    .tab-wrap {      display: flex;      position: absolute;      top: 4.06rem;      left: 2.14rem;      .list-box {        font-size: .24rem;        writing-mode: vertical-lr;        padding-right: .49rem;        padding-bottom: .12rem;        margin-right: .5rem;        cursor: pointer;        &:hover {          span {            color: #15A98D;          }        }      }      .class-one {        color: #333333;        border-right: 0.01rem solid #D2D2D2;      }      .class-two {        color: #ffffff;        &:not(:last-child) {          border-right: 0.01rem solid #ffffff;        }      }    }  }}</style>