<template>  <div class="index">    <!-- ҳͷ -->    <HeaderJp v-show="fixClass"/>    <SectionOne />    <SectionTwo />    <SectionThree />    <SectionFour />    <SectionFive />    <FooterCom />  </div></template><script>import SectionOne from './components/SectionOne.vue'import SectionTwo from './components/SectionTwo.vue'import SectionThree from './components/SectionThree.vue'import SectionFour from './components/SectionFour.vue'import SectionFive from './components/SectionFive.vue'import HeaderJp from '@/components/header/PC/HeaderJp.vue'import FooterCom from '@/components/header/PC/FooterJp.vue'export default {  name: 'japanesePC',  components: {    SectionOne,    SectionTwo,    SectionThree,    SectionFour,    SectionFive,    HeaderJp,    FooterCom  },  data () {    return {      fixClass:false    }  },  mounted () {    window.addEventListener('scroll', this.handleScroll, true)  },  methods: {    handleScroll() {      let scrolltop = window.pageYOffset || document.documentElement.scrollTop || document.body.scrollTop      let height = document.querySelector('.nav-btn') ? document.querySelector('.nav-btn').offsetHeight : ''      if(scrolltop >= height) {        if(!this.fixClass) {          this.fixClass = true        }      } else {        if(this.fixClass) {          this.fixClass = false        }      }    },  }}</script><style lang='scss' scoped></style>