<template>  <div class="list-wrap">    <div v-if="current === 'dxmkpzs'" class="list-box active">      ¿ÆÑ§ÆÕ¼°¥Ç©`¥¿¥Ù©`¥¹    </div>    <div v-for="(item, index) in tabList" :key="index" v-else class="list-box" :class="{active: current === item.channelCode}" @click="tabClick(item)">      {{ strIntercept(item.name) }}    </div>  </div></template><script>import { getChannelSubset } from '@/service/apiList/common'export default {  name: 'PCTabList',  data() {    return {      tabList: [],      current: ''    }  },  computed: {    strIntercept() {      return (item) => {        try {          return item.includes('£¨') ? item.substring(0, item.indexOf('£¨')) : item        } catch (error) {          return item        }      }    }  },  watch: {    $route: {      handler(to) {        this.getChannelSubset(to.query.channelCode)        this.current = to.query.tab        if(to.query.tab && (to.query.tab === 'zs' || to.query.tab === 'sk')) {          this.current = 'dxmkpzs'        }      },      immediate: true,      deep: true,    },  },  methods: {    tabClick(item) {      document.title = this.strIntercept(item.name)      this.current = item.channelCode      this.$emit('activeItem', this.current)      if(this.$route.meta.title === 'details') {        this.$router.push({          path: item.url1,        })      } else {        this.$router.replace({          path: item.url1,        })      }    },    async getChannelSubset(channelCode) {      const res = await getChannelSubset({        channelCode,      })      if (res.code === 0) {        this.tabList = res.data.subset        if (!this.$route.query.tab) {          this.$router.replace({            path: this.tabList[0].url1,            query: {              channelCode: this.$route.query.channelCode,              tab: this.tabList[0].channelCode,            },          })        }        this.$emit('activeItem', this.current)      }    }  }}</script><style lang="scss" scoped>.list-wrap {  width: 13.6rem;  margin: auto;  display: flex;  flex-wrap: wrap;  position: absolute;  top: 4.2rem;  left: 50%;  transform: translateX(-50%);  justify-content: center;  .list-box {    padding: 0 .2rem;    height: .4rem;    text-align: center;    line-height: .4rem;    font-size: .18rem;    border-radius: .2rem;    margin-right: .2rem;    cursor: pointer;    color: #ffffff;    &:hover {      background: #15a98d;    }  }  .active {    background: #15a98d;  }}</style>