<template>  <div class="section-two">    <img class="bg" :src="details.backgroundImg1 || $Placeholder" alt="" />    <div class="main">      <div class="img" @click="playVideo">        <img src="https://file.daqsoft.com/uploadfile/server/geekui/panda/PC/about/img05.png" alt="" />      </div>      <div class="title">基地宣传片</div>      <div class="desc">山峦含黛、碧水如镜、林涛阵阵、百鸟谐鸣</div>    </div>    <VideoPlay :url="videoUrl" :show.sync="show" />  </div></template><script>import VideoPlay from './VideoPlay.vue'import { getChannelDetail, getNewsList } from '@/service/apiList/common'export default {  name: 'sectionOne',  data () {    return {      details: {},      videoUrl: '',      show: false,    }  },  components: {    VideoPlay  },  mounted() {    this.getChannelDetail()  },  methods: {    async getChannelDetail() {      const res = await getChannelDetail({ channelCode: 'jdxcp' })      if (res.code === 0) {        this.details = res.data      }    },    // 获取视频    async playVideo() {      const res = await getNewsList({ channelCode: 'jdxcp', pageSize: 6, currPage: 1 })      if (res.code === 0) {        try {          this.show = true          this.videoUrl = res.datas[0].video.url        } catch (error) {          this.videoUrl = ''        }      }    },  }}</script><style lang="scss" scoped>.section-two {  width: 100%;  position: relative;  .bg {    width: 100%;  }  .main {    max-width: 1660px;    min-width: 1360px;    left: 50%;    transform: translateX(-50%);    top: 0;    overflow: hidden;    position: absolute;    color: #ffffff;    padding: 200px 0;    text-align: center;    img {      cursor: pointer;      width: 60px;    }    .title {      font-size: 40px;      font-weight: bold;      margin: 39px 0 29px;    }    .desc {      font-size: 30px;    }  }}</style>