<template>  <div class="section-one">    <div class="title">ÄÌ°ÖÄÌÂè</div>    <div class="main">      <div v-for="(item, index) in list" @click="PCJumpDetails(item, 'dwzj')" :key="index" class="list-box wow bounceInUp" :data-wow-delay="getDelay(index)">        <div class="img-box">          <div class="img" :style="{'background': `url(${ getUrl(item) }) center top no-repeat`, 'background-size': 'cover'}"></div>        </div>        <div class="text">          <p class="name">{{item.title}}</p>          <p class="desc">{{getDesc(item.summary)}}</p>        </div>      </div>      <div class="more-btn-one" @click="$router.push('/pc/generalList?channelCode=nbnm&banner=dwzj')">        ¸ü¶à        <MyIcon :name="'shuangjiantou'" size="10px"/>      </div>    </div>  </div></template><script>import { getNewsList } from '@/service/apiList/common'export default {  name: 'sectionOne',  inject: ['PCJumpDetails'],  data () {    return {      list: []    }  },  computed: {    getUrl() {      return (list) => {        try {          return list.imageUrls[0].url        } catch (error) {          return this.$Placeholder        }      }    },    getDelay() {      return (index) => {        try {          return `${index / 10}s`        } catch (error) {          return '0.1s'        }      }    },    getDesc() {      return (desc) => {        let reg = /\s+/g        let trimStr = desc.replace(reg,'')        return trimStr      }    }  },  mounted () {    this.getNewsList()  },  methods: {    async getNewsList() {      const res = await getNewsList({        channelCode: 'nbnm',        pageSize: 3,        currPage: 1,      })      if (res.code === 0) {        this.list = res.datas      }    }  }}</script><style lang="scss" scoped>.section-one {  width: 100%;  padding: 80px 0 80px;  background: #ffffff;  .title {    font-weight: Bold;    font-size: 40px;    color: #333333;    text-align: center;    margin-bottom: 50px;  }  .main {    max-width: 1660px;    min-width: 1360px;    margin: auto;    display: flex;    justify-content: space-between;    .list-box {      width: 32%;      max-width: 520px;      min-width: 420px;      min-height: 470px;      cursor: pointer;      &:hover {        .text {          .name {            color: #15A98D;          }        }        .img {          transform: scale(1.2);        }      }      .img-box {        width: 100%;        height: 300px;        overflow: hidden;        .img {          width: 100%;          height: 100%;          transition: all .25s linear;        }      }      .text {        padding: 43px 40px 30px 30px;        background: #F5F5F5;        overflow: hidden;        .name {          color: #333333;          font-size: 20px;          font-weight: bold;          text-align: left;          @include ellipsisLn(1);          transition: all .25s linear;          margin: 0;        }        .desc {          color: #666666;          font-size: 16px;          height: 56px;          margin: 22px 0 0;          @include ellipsisLn(2);          line-height: 28px;          text-indent: 2em;        }      }    }  }}</style>