<template>  <div class="section-three" v-if="list.length">    <div class="main">      <div v-for="(item, index) in list" :key="index" class="list-box wow" :class="index === 0 ? 'bounceInLeft' : 'bounceInRight'" @click="jump(item)" :style="{'background': `url(${ getUrl(item) }) center top no-repeat`, 'background-size': 'cover'}">        <div class="list-content">          <div class="title">{{ item.name }}</div>          <div class="desc">{{ item.summary }}</div>          <div class="line"></div>          <div class="btn">            <img src="https://file.daqsoft.com/uploadfile/server/geekui/panda/PC/animal/img11.png" alt="" />          </div>        </div>      </div>    </div>  </div></template><script>import { getNewsList, getChannelDetail } from '@/service/apiList/common'export default {  name: 'sectionThree',  inject: ['PCJumpDetails'],  data () {    return {      list: []    }  },  computed: {    getUrl() {      return (item) => {        try {          return item.backgroundImg        } catch (error) {          return this.$Placeholder        }      }    },  },  created() {    this.hospital()  },  methods: {    hospital() {      const p1 = getChannelDetail({        channelCode: 'jbfz',      }).then((res) => {        return res.data      })      const p2 = getChannelDetail({        channelCode: 'xmyy',      }).then((res) => {        return res.data      })      Promise.all([p1, p2]).then((res) => {        try {          this.list = res        } catch (error) {          this.list = []        }      })    },    jump(item) {      getNewsList({        channelCode: item.channelCode,      }).then((res) => {        if (res.code === 0 && res.datas) {          try {            this.PCJumpDetails(res.datas[0], 'dwzj')          } catch (error) {            this.$message({              message: '¾´ÇëÆÚ´ý',              type: 'warning'            })          }        } else {          this.$message({            message: '¾´ÇëÆÚ´ý',            type: 'warning'          })        }      })    }  }}</script><style lang="scss" scoped>.section-three {  width: 100%;  padding: 80px 0;  background: #ffffff;  .main {    max-width: 1660px;    min-width: 1360px;    margin: auto;    display: flex;    justify-content: space-between;    .list-box {      width: 49%;      max-width: 790px;      min-width: 660px;      height: 320px;      overflow: hidden;      cursor: pointer;      &:hover {        .list-content {          .line {            background: rgba(255, 255, 255, 1);          }        }      }      .list-content {        width: 85%;        margin: 69px auto 0;        color: #ffffff;        position: relative;        .title {          font-weight: bold;          font-size: 40px;        }        .desc {          height: 60px;          font-size: 20px;          width: 80%;          line-height: 32px;          margin: 60px 0 20px;        }        .line {          width: 100%;          height: 1px;          background: rgba(255, 255, 255, 0.4);        }        .btn {          position: absolute;          right: 0;          bottom: 40px;        }      }    }  }}</style>