<template>  <div class="section-two">    <img class="bg" :src="bg || $Placeholder" alt="" />    <div class="main">      <div class="title">{{ details.channelName }}</div>      <div class="desc">{{ details.summary }}</div>      <div class="list-wrap">        <div class="list-box wow bounceInUp" :data-wow-delay="getDelay(index)" v-for="(item, index) in list" :key="index">          <div class="icon">            <img :src="item.icon" alt="" />          </div>          <p class="name">{{ item.name }}</p>        </div>      </div>      <div class="more-btn-three" @click="PCJumpDetails(details, 'dwzj')">        更多        <MyIcon :name="'shuangjiantou'" size="10px"/>      </div>    </div>  </div></template><script>import { getNewsList } from '@/service/apiList/common'export default {  name: 'sectionTwo',  inject: ['PCJumpDetails'],  data () {    return {      bg: 'https://file.daqsoft.com/uploadfile/server/geekui/panda/PC/big/img05.jpg',      list: [        /*{          icon: 'https://file.daqsoft.com/uploadfile/server/geekui/panda/PC/animal/img05.png',          name: '分布板块'        },*/        {          icon: 'https://file.daqsoft.com/uploadfile/server/geekui/panda/PC/animal/img06.png',          name: '常用竹（笋）'        },        {          icon: 'https://file.daqsoft.com/uploadfile/server/geekui/panda/PC/animal/img07.png',          name: '大熊猫丰容'        },        {          icon: 'https://file.daqsoft.com/uploadfile/server/geekui/panda/PC/animal/img08.png',          name: '动物训练'        }      ],      details: {}    }  },  computed: {    getDelay() {      return (index) => {        try {          return `${index / 10}s`        } catch (error) {          return '0.1s'        }      }    }  },  created() {    this.getNewsList()  },  methods: {    async getNewsList() {      const res = await getNewsList({        channelCode: 'sydt',        pageSize: 1,        currPage: 1,      })      if (res.code === 0) {        if (res.datas && res.datas.length) {          this.details = res.datas[0]        }      }    }  }}</script><style lang="scss" scoped>.section-two {  width: 100%;  position: relative;  .bg {    width: 100%;    min-height: 600px;  }  .main {    max-width: 1660px;    min-width: 1360px;    width: 100%;    left: 50%;    transform: translateX(-50%);    top: 0;    overflow: hidden;    position: absolute;    color: #ffffff;    .title {      font-weight: Bold;      font-size: 50px;      text-align: left;      margin: 118px 0 50px;    }    .desc {      width: 705px;      font-size: 24px;    }    .list-wrap {      width: 500px;      margin-top: 60px;      display: flex;      justify-content: space-between;      .list-box {        display: flex;        flex-wrap: wrap;        justify-content: flex-start;        .icon {          width: 90px;          height: 90px;          background: rgba(255, 255, 255, 0.2);          border-radius: 50%;          margin-bottom: 25px;          position: relative;          img {            display: block;            position: absolute;            left: 50%;            top: 50%;            transform: translate(-50%, -50%);            width: 44px;          }        }        .name {          font-size: 20px;        }      }    }    .more-btn-three {      font-size: 16px;      margin-top: 60px;      cursor: pointer;      color: #ffffff;      border-bottom: 1px solid transparent;      display: inline-flex;      align-items: center;      i {        margin-left: 5px;        position: relative;        top: 1px;      }      &:hover {        border-bottom-color: #ffffff;      }    }  }}</style>