<template>  <div class="section-two"       :style="{'background-image': `url(${bg || $Placeholder})`, 'background-repeat': 'no-repeat', 'background-size': 'cover', 'background-position-y': 'bottom'}">    <div class="main">      <div class="title wow fadeIn" @click="$router.push('/pc/baseDynamic/list?channelCode=xmqw')">熊猫趣闻</div>      <div class="swiper-wrap wow fadeIn">        <swiper class="my-swiper" :options="swiperOption" ref="mySwiper2">          <swiper-slide v-for="(item, index) in swiperList" :key="index">            <div @click.stop="PCJumpDetails(item, 'jddt')">              <div class="img" :style="{'background': `url(${getUrl(item)}) center top no-repeat`, 'background-size': 'cover'}" v-show="item.imageUrls[0]"></div>              <div class="text">                <div class="title">{{ item.title }}</div>                <div class="desc">{{ getDesc(item.summary) }}</div>              </div>            </div>          </swiper-slide>          <template v-slot:pagination>            <div class="swiper-pagination"></div>          </template>        </swiper>        <div class="more-btn-two" @click="$router.push('/pc/baseDynamic/list?channelCode=xmqw')">          更多          <MyIcon :name="'shuangjiantou'" size="10px"/>        </div>      </div>    </div>  </div></template><script>import { getNewsList } from '@/service/apiList/common'export default {  name: 'sectionTwo',  inject: ['PCJumpDetails'],  data () {    // const _this = this    return {      bg: 'https://file.daqsoft.com/uploadfile/server/geekui/panda/PC/big/img01.jpg',      swiperOption: {        effect: 'fade',        slidesPerView: 'auto',        loop: true,        // 显示分页        pagination: {          el: '.swiper-pagination',          clickable: true,        },        autoplay: {          delay: 3000,          stopOnLastSlide: true,          disableOnInteraction: false,        },        fadeEffect: {          crossFade: true,        }      },      swiperList: []    }  },  mounted() {    this.getNewsList()  },  computed: {    getUrl() {      return (list) => {        try {          return list.imageUrls[0].url        } catch (error) {          return this.$Placeholder        }      }    },    getDesc() {      return (desc) => {        let reg = /\s+/g        let trimStr = desc.replace(reg,'')        return trimStr      }    }  },  methods: {    async getNewsList() {      const res = await getNewsList({        channelCode: 'xmqw',        pageSize: 5,        currPage: 1,      })      if (res.code === 0) {        this.swiperList = res.datas      }    },  }}</script><style lang="scss" scoped>.section-two {  width: 100%;  height: 760px;  .main {    max-width: 1660px;    min-width: 1360px;    margin: auto;    position: relative;    .title {      font-weight: Bold;      color: #ffffff;      font-size: 50px;      text-align: left;      margin: 118px 0 50px;      cursor: pointer;      display: inline-block;    }    .swiper-pagination-bullets {      bottom: 140px;      display: flex;      align-items: center;      justify-content: center;      ::v-deep .swiper-pagination-bullet {        width: 12px;        height: 12px;        display: inline-block;        border-radius: 100%;        background: none;        border: 2px solid rgba(255, 255, 255, 0.5);        margin: 0 18px;        opacity: 1;      }      ::v-deep .swiper-pagination-bullet-active {        width: 20px;        height: 20px;        display: inline-block;        border-radius: 100%;        border: 3px solid #ffffff;        opacity: 1;      }    }    .swiper-wrap {      width: 580px;      height: 440px;      background: #ffffff;      padding: 30px 0 0;      position: relative;      .my-swiper {        width: 520px;        height: 100%;        cursor: pointer;        .img {          width: 100%;          height: 292px;        }        .text {          .title {            font-weight: bold;            font-size: 20px;            color: #333333;            margin: 25px 0 19px;            transition: 0.25s all;          }          .desc {            margin: 0;            font-size: 16px;            color: #666666;            @include ellipsisLn(1);            text-indent: 2em;          }        }        &:hover {          .title {            color: #15a98d;          }        }      }    }  }}</style>