<template>  <div class="section-two">    <div class="bg">      <div class="title">国际合作</div>      <div class="img">        <img src="https://file.daqsoft.com/uploadfile/server/geekui/panda/PC/cooperation/map2.png" alt="" />      </div>      <div class="desc">实验室与全球保护机构繁育合作网络</div>    </div>    <div class="main">      <div class="desc" v-if="details.summary" v-html="getText(details.summary)"></div>      <div class="list-wrap">        <div v-for="(item, index) in list" :key="index" class="list-box wow bounceInUp" :data-wow-delay="getDelay(index)">          <div class="icon" :style="{'background': item.bgColor}">            <img :src="item.cover" alt="" />          </div>          <div class="name">{{item.name}}</div>        </div>      </div>      <div class="more" @click="jump">查看更多</div>    </div>  </div></template><script>import { getNewsList, getChannelDetail } from '@/service/apiList/common'export default {  name: 'sectionOne',  inject: ['PCJumpDetails'],  data () {    return {      mainText: '根据国家重大政治和外交的需要，\n' +        '在国家、省市的领导下熊猫基地先后与日本、美国、西班牙、法国建立了“大熊猫长期国际合作繁殖计划”，\n' +        '成功开展了大熊猫繁育、动物行为学和保护教育等多个领域的合作与研究，共合作繁殖大熊猫15只，取得了国际大熊猫合作繁殖产仔数量最多的优异成绩。',      list: [        {          name: '繁育合作',          cover: 'https://file.daqsoft.com/uploadfile/server/geekui/panda/PC/cooperation/img04.png',          bgColor: '#F15929'        },        {          name: '科研合作',          cover: 'https://file.daqsoft.com/uploadfile/server/geekui/panda/PC/cooperation/img05.png',          bgColor: '#501A74'        },        {          name: '合作平台',          cover: 'https://file.daqsoft.com/uploadfile/server/geekui/panda/PC/cooperation/img06.png',          bgColor: '#00AEEF'        },        {          name: '交流活动与人才培养',          cover: 'https://file.daqsoft.com/uploadfile/server/geekui/panda/PC/cooperation/img07.png',          bgColor: '#15A98D'        }      ],      details: {}    }  },  computed: {    getText() {      return (text) => {        if (text && text.length > 69) {          return `<span>${text.substring(0, 69)}</span><span>${text.substring(69, text.length)}</span>`        }        return `<span>${text}</span>`      }    },    getDelay() {      return (index) => {        try {          return `${index / 10}s`        } catch (error) {          return '0.1s'        }      }    }  },  mounted() {    this.getNewsList()  },  methods: {    async getNewsList() {      const res = await getChannelDetail({        channelCode: 'gjhz',      })      if (res.code === 0) {        this.details = res.data      }    },    // 跳转    jump() {      getNewsList({        channelCode: 'gjhz',      }).then((res) => {        try {          this.PCJumpDetails(res.datas[0], 'dwhz')        } catch (error) {          this.$message.error(error)        }      })    }  }}</script><style lang="scss" scoped>.section-two {  width: 100%;  background: #F5F5F5;  height: 1262px;  .bg {    width: 100%;    height: 900px;    background: #15A98D;    position: relative;    color: #ffffff;    overflow: hidden;    .title {      font-weight: Bold;      text-align: center;      font-size: 40px;      margin: 79px 0 434px;    }    .desc {      font-weight: 500;      opacity: 0.9;      font-size: 24px;      width: 80%;      max-width: 1300px;      min-width: 1200px;      margin: auto;    }    .img {      position: absolute;      top: 170px;      left: 50%;      transform: translateX(-50%);    }  }  .main {    max-width: 1660px;    min-width: 1360px;    margin: auto;    background: url("https://file.daqsoft.com/uploadfile/server/geekui/panda/PC/cooperation/img08.png") no-repeat;    background-size: cover;    height: 600px;    position: relative;    top: -238px;    padding: 80px 0 0;    .desc {      text-align: center;      color: #666666;      font-size: 20px;      margin: 0 auto 50px;      line-height: 32px;      min-width: 1360px;      ::v-deep span {        text-align: center;        display: block;        &:nth-child(2) {          width: 50%;          @include ellipsis(50%);          margin: auto;        }      }    }    .list-wrap {      display: flex;      justify-content: center;      .list-box {        text-align: center;        margin: 0 70px;        .icon {          width: 100px;          height: 100px;          border-radius: 10px;          position: relative;          margin: 0 auto 24px;          img {            position: absolute;            top: 50%;            left: 50%;            transform: translate(-50%, -50%);            width: 44px;          }        }        .name {          color: #333333;          font-size: 20px;        }      }    }    .more {      margin: 50px auto;      width: 180px;      height: 50px;      background: rgb(230, 230, 230);      text-align: center;      line-height: 50px;      font-size: 20px;      color: #333333;      cursor: pointer;      &:hover {        color: #ffffff;        background: #15a98d;      }    }  }}</style>