<template>  <div class="section-four" v-if="list.length">    <div class="title">科普资料库</div>    <div class="main">      <div class="swiper-wrap">        <swiper :options="linkSwiperOptions">          <swiper-slide v-for="(item, index) in list" :key="index" :class="{marginAuto: list.length < 5}">            <div class="box" @click="PCJumpDetails(item, 'kpjy')">              <div class="img-box wow bounceInDown" :data-wow-delay="getDelay(index)">                <img class="cloud-img" :src="getUrl(item)"/>              </div>              <div class="name">{{ item.title }}</div>            </div>          </swiper-slide>        </swiper>        <div class="swiper-button btn-left" v-show="list.length > 5">          <img src="https://file.daqsoft.com/uploadfile/server/geekui/panda/PC/education/left.png" alt="" />        </div>        <div class="swiper-button btn-right" v-show="list.length > 5">          <img src="https://file.daqsoft.com/uploadfile/server/geekui/panda/PC/education/right.png" alt="" />        </div>      </div>      <div class="booth">        <img src="https://file.daqsoft.com/uploadfile/server/geekui/panda/PC/education/img10.png" alt="" />      </div>      <div class="more-btn-one" @click="$router.push('/pc/education/list')">        更多        <MyIcon :name="'shuangjiantou'" size="10px"/>      </div>    </div>  </div></template><script>import { getNewsList } from '@/service/apiList/common'export default {  name: 'sectionFour',  data () {    return {      linkSwiperOptions: {        slidesPerView : 5,        spaceBetween : 100,        loop: false,        navigation: {          prevEl: '.btn-left',          nextEl: '.btn-right',        }      },      list: []    }  },  computed: {    getUrl() {      return (list) => {        try {          return list.imageUrls[0].url        } catch (error) {          return this.$Placeholder        }      }    },    getDelay() {      return (index) => {        try {          return `${index / 10}s`        } catch (error) {          return '0.1s'        }      }    }  },  mounted() {    this.getNewsList()  },  methods: {    async getNewsList() {      const res = await getNewsList({        channelCode: 'zl',        pageSize: 10,        currPage: 1,      })      if (res.code === 0) {        this.list = res.datas        document.querySelector('.swiper-wrapper') ? document.querySelector('.swiper-wrapper').style.justifyContent = 'space-between' : ''      }    },    PCJumpDetails(item, channelCode) {      this.$router.push({        path: '/pc/details',        query: {          id: item.id,          channelCode: channelCode,          tab: 'kpzlk'        },      })    },  }}</script><style lang="scss" scoped>.section-four {  width: 100%;  padding: 0 0 80px ;  background: #ffffff;  position: relative;  .title {    font-weight: Bold;    font-size: 40px;    color: #333333;    text-align: center;    margin-bottom: 60px;  }  .main {    max-width: 1660px;    min-width: 1360px;    margin: auto;    position: relative;    .swiper-wrap {      width: 90%;      margin: auto;      position: relative;      z-index: 9;      .marginAuto {        margin: auto !important;      }      .swiper-slide {        width: 209px;        cursor: pointer;        &:hover {          .name {            color: #15A98D;          }        }        .box {          width: 100%;          img {            width: 100%;          }        }        .name {          text-align: center;          font-weight: Bold;          font-size: 18px;          color: #333333;          margin-top: 65px;          transition: all .25s;        }      }      .swiper-button {        position: absolute;        top: 50%;        cursor: pointer;        transform: translateY(-100%);      }      .btn-left {        left: -80px;      }      .btn-right {        right: -80px;      }    }    .booth {      width: 100%;      position: absolute;      left: 0;      bottom: 50px;      z-index: 5;      img {        width: 100%;      }    }  }}</style>