sc) => {        let reg = /\s+/g        let trimStr = desc.replace(reg,'')        return trimStr      }    }  },  mounted() {    this.getNewsList()  },  methods: {    async getNewsList() {      const res = await getNewsList({        channelCode: 'kpdt',        pageSize: 4,        currPage: 1,      })      if (res.code === 0) {        if (res.datas.length) {          this.list = res.datas        }      }    },  }}</script><style lang="scss" scoped>.section-three {  width: 100%;  padding: 80px 0;  background: #ffffff;  .title {    font-weight: Bold;    font-size: 40px;    color: #333333;    text-align: center;    margin-bottom: 60px;  }  .main {    max-width: 1660px;    min-width: 1360px;    margin: auto;    display: flex;    justify-content: space-between;    .left, .right {      width: 49%;      max-width: 790px;      min-width: 660px;      height: 635px;      .text {        padding: 36px 44px 29px 30px;        background: rgba(245,245,245,1);        height: 180px;        overflow: hidden;        .title {          color: #333333;          font-size: 20px;          font-weight: bold;          text-align: left;          margin-bottom: 0;          @include ellipsisLn(1);          transition: all .25s;        }        .desc {          color: #666666;          font-size: 16px;          margin: 19px 0 20px;          @include ellipsisLn(2);          text-indent: 2em;        }        .time {          color: #999999;          font-size: 14px;        }      }    }    .left {      &:hover {        .title {          color: #15A98D;        }      }      cursor: pointer;      .img-box {        width: 100%;        height: 455px;      }    }    .right {      display: flex;      align-content: space-between;      flex-wrap: wrap;      .right-list-box {        width: 100%;        height: 190px;        cursor: pointer;        &:hover {          .title {            color: #15A98D;