<template>  <full-page :options="options" ref="fullPage" class="fullPage">    <section class="section wow flash">      <Section1 :indexChild="indexNum" />    </section>    <section class="section wow flash">      <Section2 :indexChild="indexNum" />    </section>    <section class="section">      <Section3 :indexChild="indexNum" />    </section>    <section class="section">      <Section4 :indexChild="indexNum" />    </section>    <section class="section">      <Section5 :indexChild="indexNum" />    </section>    <section class="section">      <Section6 :indexChild="indexNum" />      <FooterCom />    </section>  </full-page></template><script>import Vue from 'vue'import 'fullpage.js/vendors/scrolloverflow'import VueFullpage from 'vue-fullpage.js'Vue.use(VueFullpage)import Section1 from '@/views/PC/index/components/Section1.vue'import Section2 from '@/views/PC/index/components/Section2.vue'import Section3 from '@/views/PC/index/components/Section3.vue'import Section4 from '@/views/PC/index/components/Section4.vue'import Section5 from '@/views/PC/index/components/Section5.vue'import Section6 from '@/views/PC/index/components/Section6.vue'import FooterCom from '@/components/header/PC/footer.vue'export default {  name: 'IndexHome',  components: {    Section1,    Section2,    Section3,    Section4,    Section5,    Section6,    FooterCom,  },  data() {    return {      indexNum: 0,      options: {        scrollOverflow: true,        afterLoad: function (anchorLink,index) {          this.indexNum = index.index        }      },      watchValue: ''    }  },  watch: {    indexNum: {      handler (value) {        this.watchValue = value      },      deep: true    }  }}</script><style scoped>.fullPage {  background-color: #343434;}::v-deep .iScrollVerticalScrollbar {  opacity: 0;}</style>