<template>  <div class="section-one">    <div class="title" @click="$router.push('/pc/generalList?channelCode=xmyst&banner=xmwh')">–‹√®“’ ıÕ≈</div>    <div class="main">      <div class="swiper-wrap" v-if="list.length">        <swiper :options="linkSwiperOptions">          <swiper-slide v-for="(item, index) in list" :key="index" class="swiper-no-swiping">            <div class="box" @click="PCJumpDetails(item, 'xmwh')">              <div class="img-box">                <img class="cloud-img" :src="getUrl(item)"/>              </div>            </div>          </swiper-slide>        </swiper>        <div class="swiper-button btn-left" v-if="list.length > 1">          <MyIcon :name="'xianxingzuojiantou'" size="60px"/>        </div>        <div class="swiper-button btn-right" v-if="list.length > 1">          <MyIcon :name="'xianxingyoujiantou'" size="60px"/>        </div>      </div>      <div class="swiper-wrap2" v-if="list.length">        <!--<swiper :options="linkSwiperOptions2">          <swiper-slide v-for="(item, index) in list" :key="index" class="swiper-no-swiping">          </swiper-slide>        </swiper>-->        <div class="box">          <div class="text" v-for="(item, index) in list" :key="index" v-show="index === swiperIndex">            <div class="name">{{item.title}}</div>            <div class="desc">{{item.summary}}</div>          </div>        </div>      </div>    </div>  </div></template><script>import { getNewsList } from '@/service/apiList/common'export default {  name: 'sectionOne',  inject: ['PCJumpDetails'],  data () {    const _this = this    return {      linkSwiperOptions: {        slidesPerView : 5,        spaceBetween: '9.95%',        centeredSlides: true,        loop: false,        initialSlide: 2,        noSwiping: true,        preventInteractionOnTransition : true,        navigation: {          prevEl: '.btn-left',          nextEl: '.btn-right',        },        on: {          slideChange: function () {            _this.swiperIndex = this.activeIndex          },        },      },      linkSwiperOptions2: {        effect: 'fade',        loop: false,        initialSlide: 2,        fadeEffect: {          crossFade: true,        },        noSwiping: true,        navigation: {          prevEl: '.btn-left',          nextEl: '.btn-right',        }      },      list: [],      swiperIndex: 2    }  },  computed: {    getUrl() {      return (list) => {        try {          return list.imageUrls[0].url        } catch (error) {          return this.$Placeholder        }      }    },  },  mounted() {    this.getNewsList()  },  methods: {    async getNewsList() {      const res = await getNewsList({ channelCode: 'xmyst' })      if (res.code === 0) {        this.list = res.datas      }    },  }}</script><style lang="scss" scoped>.section-one {  width: 100%;  padding: 80px 0;  background: #ffffff;  text-align: center;  .title {    display: inline-block;    font-weight: Bold;    font-size: 40px;    color: #333333;    text-align: center;    margin-bottom: 60px;    cursor: pointer;  }  .main {    max-width: 1660px;    min-width: 1360px;    margin: auto;    .swiper-wrap {      width: 90%;      margin: auto;      position: relative;      z-index: 9;      ::v-deep .swiper-wrapper {        align-items: center;      }      ::v-deep .swiper-container {        height: 400px;      }      ::v-deep .swiper-slide {        width: auto !important;      }      ::v-deep .swiper-slide-active{        transform: scale(2);        position: relative;        background: #ffffff;        box-shadow: 0 0 20px -15px #000000;        border-radius: 50%;        .box {          width: 100%;          .img-box {            transform: scale(0.9);            border: 1px dotted rgba(51, 51, 51, 0.1);            border-radius: 50%;          }          img {            display: block;            transform: scale(0.9);          }        }      }      .swiper-slide {        height: auto;        .box {          width: 100%;          cursor: pointer;          .img-box {            width: 180px;            height: 180px;            img {              width: 100%;              height: 100%;              border-radius: 50%;            }          }        }        .name {          text-align: center;          font-weight: Bold;          font-size: 18px;          color: #333333;          margin-top: 65px;        }      }      .swiper-button {        position: absolute;        top: 50%;        cursor: pointer;        transform: translateY(-50%);        color: #d8d8d8;        &:hover {          color: #15A98D;        }      }      .btn-left {        left: -80px;      }      .btn-right {        right: -80px;      }    }    .swiper-wrap2 {      text-align: center;      .name {        color: #333333;        font-weight: bold;        font-size: 24px;        margin: 40px 0 20px;      }      .desc {        font-size: 20px;        color: #666666;        @include ellipsisLn(1);      }    }  }}</style>