<template>  <div class="section-two" v-if="list.length">    <div class="title">要闻速递</div>    <div class="main">      <div v-for="(item, index) in list" :key="index" class="list-box wow" @click="PCJumpDetails(item, 'xmg')"           :class="index === 0 ? 'bounceInLeft' : (index === 2 ? 'bounceInRight' : 'bounceInUp')">        <div v-if="index !== 1">          <div class="img-box">            <div class="img" :style="{'background': `url(${ getUrl(item) }) no-repeat`, 'background-size': 'cover'}"></div>          </div>          <div class="text">            <p class="title">{{ item.title }}</p>            <p class="desc">{{ getDesc(item.summary) }}</p>            <p class="time">{{ item.publishTime }}</p>          </div>        </div>        <div v-else>          <div class="text">            <p class="title">{{ item.title }}</p>            <p class="desc">{{ getDesc(item.summary) }}</p>            <p class="time">{{ item.publishTime }}</p>          </div>          <div class="img-box">            <div class="img" :style="{'background': `url(${ getUrl(item) }) no-repeat`, 'background-size': 'cover'}"></div>          </div>        </div>      </div>      <div class="more-btn-one" @click="$router.push('/pc/generalList?channelCode=ywsd&banner=xmg')">        更多        <MyIcon :name="'shuangjiantou'" size="10px"/>      </div>    </div>  </div></template><script>import { getNewsList } from '@/service/apiList/common'export default {  name: 'sectionOne',  inject: ['PCJumpDetails'],  data () {    return {      list: [],      details: {}    }  },  computed: {    getUrl() {      return (list) => {        try {          return list.imageUrls[0].url        } catch (error) {          return this.$Placeholder        }      }    },    getDesc() {      return (desc) => {        let reg = /\s+/g        let trimStr = desc.replace(reg,'')        return trimStr      }    }  },  mounted() {    this.getNewsList()  },  methods: {    async getNewsList() {      const res = await getNewsList({        channelCode: 'ywsd',        pageSize: 3,        currPage: 1,      })      if (res.code === 0) {        this.list = res.datas      }    }  }}</script><style lang="scss" scoped>.section-two {  width: 100%;  padding: 80px 0;  background: #ffffff;  .title {    font-weight: Bold;    font-size: 40px;    color: #333333;    text-align: center;    margin-bottom: 58px;  }  .main {    max-width: 1660px;    min-width: 1360px;    margin: auto;    display: flex;    .list-box {      width: 34%;      max-width: 560px;      min-width: 450px;      cursor: pointer;      &:hover {        .text {          .title {            color: #15A98D;          }        }        .img-box {          .img {            transform: scale(1.2);          }        }      }      .img-box {        height: 315px;        overflow: hidden;        .img {          width: 100%;          height: 100%;          display: block;          transition: all .25s;        }      }      .text {        height: 315px;        padding: 70px 70px 0 30px;        background: #F5F5F5;        .title {          color: #333333;          font-size: 20px;          font-weight: bold;          text-align: left;          @include ellipsisLn(1);          margin-bottom: 0;          transition: all .25s;        }        .desc {          color: #666666;          font-size: 16px;          height: 47px;          margin: 30px 0 58px;          line-height: 25px;          @include ellipsisLn(2);          text-indent: 2em;        }        .time {          color: #999999;          font-size: 14px;        }      }    }  }}</style>