<template>  <div class="section-two">    <div class="main">      <div class="list-box wow"           v-for="(item, index) in list" :key="index" @click="jump(item)"           :class="index === 0 ? 'bounceInLeft' : 'bounceInRight'">        <img :src="getUrl(item)" alt="" />      </div>    </div>  </div></template><script>import { getNewsList, getChannelDetail } from '@/service/apiList/common'export default {  name: 'sectionTwo',  inject: ['PCJumpDetails'],  data () {    return {      leftBg: 'https://file.daqsoft.com/uploadfile/server/geekui/panda/PC/big/img03.jpg',      rightBg: 'https://file.daqsoft.com/uploadfile/server/geekui/panda/PC/big/img04.jpg',      list: []    }  },  computed: {    getUrl() {      return (item) => {        try {          return item.backgroundImg1        } catch (error) {          return this.$Placeholder        }      }    },  },  mounted () {    this.getImg()  },  methods: {    getImg() {      const res1 = getChannelDetail({        channelCode: 'yjfxjnr',      }).then((res) => {        return res.data      })      const res2 = getChannelDetail({        channelCode: 'kyxmjcg',      }).then((res) => {        return res.data      })      Promise.all([res1, res2]).then((res) => {        this.list = res      })    },    jump(item) {      getNewsList({ channelCode: item.channelCode }).then((res) => {        if (res.code === 0) {          try {            this.PCJumpDetails(res.datas[0], 'kybh')          } catch (error) {            return ''          }        }      })    }  }}</script><style lang="scss" scoped>.section-two {  width: 100%;  .main {    width: 100%;    height: 100%;    display: flex;    .left, .right, .list-box {      height: 100%;      width: 50%;      cursor: pointer;      img {        width: 100%;      }    }  }}</style>