<template>  <div class="section-three">    <div class="title">爱心名录</div>    <div class="main">      <div class="desc" v-if="loveDetails.summary" v-html="getText(loveDetails.summary)"></div>      <div class="img">        <img :src="loveDetails.backgroundImg1" alt="" />      </div>    </div>  </div></template><script>import { getChannelDetail } from '@/service/apiList/common'export default {  name: 'sectionOne',  data () {    return {      loveDetails: {}    }  },  mounted() {    this.getLoveDetail()  },  computed: {    getText() {      return (text) => {        if (text && text.length > 69) {          return `<span>${text.substring(0, 69)}</span><span>${text.substring(69, text.length)}</span>`        }        return `<span>${text}</span>`      }    },  },  methods: {    // 爱心名录    async getLoveDetail() {      const res = await getChannelDetail({        channelCode: 'axml',      })      if (res.code === 0) {        this.loveDetails = res.data      }    }  }}</script><style lang="scss" scoped>.section-three {  width: 100%;  padding: 80px 0;  background: #ffffff;  .title {    font-weight: Bold;    font-size: 40px;    color: #333333;    text-align: center;  }  .main {    max-width: 1660px;    min-width: 1360px;    margin: auto;    .desc {      color: #666666;      font-size: 20px;      text-align: center;      line-height: 32px;      margin: 40px 0;      ::v-deep span {        text-align: center;        display: block;        &:nth-child(2) {          width: 50%;          @include ellipsis(50%);          margin: auto;        }      }    }    .img {      width: 90%;      margin: 48px auto 0;      img {        width: 100%;      }    }  }}</style>